 555â†’function findAvailablePort(
 556â†’  startPort: number,
 557â†’  host: string = '127.0.0.1'
 558â†’): Promise<number> {
 559â†’  return new Promise((resolve, reject) => {
 560â†’    const server = net.createServer();
 561â†’
 562â†’    server.listen(startPort, host, () => {
 563â†’      const port = (server.address() as net.AddressInfo).port;
 564â†’      server.close(() => {
 565â†’        resolve(port);
 566â†’      });
 567â†’    });
 568â†’
 569â†’    server.on('error', (err: NodeJS.ErrnoException) => {
 570â†’      if (err.code === 'EADDRINUSE') {
 571â†’        // Port is in use, try the next one
 572â†’        findAvailablePort(startPort + 1, host)
 573â†’          .then(resolve)
 574â†’          .catch(reject);
 575â†’      } else {
 576â†’        reject(err);
 577â†’      }
 578â†’    });
 579â†’  });
 580â†’}
