 582â†’async function startServer(
 583â†’  html: string,
 584â†’  environmentJs: string,
 585â†’  host: string,
 586â†’  port = 4211,
 587â†’  watchForChanges = true,
 588â†’  affected: string[] = [],
 589â†’  focus: string = null,
 590â†’  groupByFolder: boolean = false,
 591â†’  exclude: string[] = []
 592â†’) {
 593â†’  let unregisterFileWatcher: (() => void) | undefined;
 594â†’
 595â†’  if (watchForChanges && !daemonClient.enabled()) {
 596â†’    output.warn({
 597â†’      title:
 598â†’        'Nx Daemon is not enabled. Graph will not refresh on file changes.',
 599â†’    });
 600â†’  }
 601â†’
 602â†’  if (watchForChanges && daemonClient.enabled()) {
 603â†’    unregisterFileWatcher = await createFileWatcher();
 604â†’  }
 605â†’
 606â†’  const { projectGraphClientResponse, sourceMapResponse } =
 607â†’    await createProjectGraphAndSourceMapClientResponse(affected);
 608â†’
 609â†’  currentProjectGraphClientResponse = projectGraphClientResponse;
 610â†’  currentProjectGraphClientResponse.focus = focus;
 611â†’  currentProjectGraphClientResponse.groupByFolder = groupByFolder;
 612â†’  currentProjectGraphClientResponse.exclude = exclude;
 613â†’
 614â†’  currentSourceMapsClientResponse = sourceMapResponse;
 615â†’
 616â†’  const app = http.createServer(async (req, res) => {
 617â†’    // parse URL
 618â†’    const parsedUrl = new URL(req.url, `http://${host}:${port}`);
 619â†’    // extract URL path
 620â†’    // Avoid https://en.wikipedia.org/wiki/Directory_traversal_attack
 621â†’    // e.g curl --path-as-is http://localhost:9000/../fileInDanger.txt
 622â†’    // by limiting the path to current directory only
 623â†’
 624â†’    res.setHeader('Access-Control-Allow-Origin', '*');
 625â†’
 626â†’    const sanitizePath = basename(parsedUrl.pathname);
 627â†’    if (sanitizePath === 'project-graph.json') {
 628â†’      res.writeHead(200, { 'Content-Type': 'application/json' });
 629â†’      res.end(JSON.stringify(currentProjectGraphClientResponse));
 630â†’      return;
 631â†’    }
 632â†’
 633â†’    if (sanitizePath === 'task-graph.json') {
 634â†’      const projectName = parsedUrl.searchParams.get('project');
 635â†’      const targetName = parsedUrl.searchParams.get('target');
 636â†’      const configuration = parsedUrl.searchParams.get('configuration');
 637â†’
 638â†’      res.writeHead(200, { 'Content-Type': 'application/json' });
 639â†’
 640â†’      if (projectName && targetName) {
 641â†’        // Lazy load specific task graph
 642â†’        res.end(
 643â†’          JSON.stringify(
 644â†’            await createSpecificTaskGraphResponse(
 645â†’              projectName,
 646â†’              targetName,
 647â†’              configuration
 648â†’            )
 649â†’          )
 650â†’        );
 651â†’      } else {
 652â†’        // Load all task graphs (legacy behavior)
 653â†’        res.end(JSON.stringify(await createTaskGraphClientResponse()));
 654â†’      }
 655â†’      return;
 656â†’    }
 657â†’
 658â†’    if (sanitizePath === 'task-graph-metadata.json') {
 659â†’      res.writeHead(200, { 'Content-Type': 'application/json' });
 660â†’      res.end(JSON.stringify(await createTaskGraphMetadataResponse()));
 661â†’      return;
 662â†’    }
 663â†’
 664â†’    if (sanitizePath === 'task-inputs.json') {
 665â†’      performance.mark('task input generation:start');
 666â†’
 667â†’      const taskId = parsedUrl.searchParams.get('taskId');
 668â†’      res.writeHead(200, { 'Content-Type': 'application/json' });
 669â†’      const inputs = await getExpandedTaskInputs(taskId);
 670â†’      performance.mark('task input generation:end');
 671â†’
 672â†’      res.end(JSON.stringify({ [taskId]: inputs }));
 673â†’      performance.measure(
 674â†’        'task input generation',
 675â†’        'task input generation:start',
 676â†’        'task input generation:end'
 677â†’      );
 678â†’
 679â†’      return;
 680â†’    }
 681â†’
 682â†’    if (sanitizePath === 'source-maps.json') {
 683â†’      res.writeHead(200, { 'Content-Type': 'application/json' });
 684â†’      res.end(JSON.stringify(currentSourceMapsClientResponse));
 685â†’      return;
 686â†’    }
 687â†’
 688â†’    if (sanitizePath === 'currentHash') {
 689â†’      res.writeHead(200, { 'Content-Type': 'application/json' });
 690â†’      res.end(JSON.stringify({ hash: currentProjectGraphClientResponse.hash }));
 691â†’      return;
 692â†’    }
 693â†’
 694â†’    if (sanitizePath === 'environment.js') {
 695â†’      res.writeHead(200, { 'Content-Type': 'application/javascript' });
 696â†’      res.end(environmentJs);
 697â†’      return;
 698â†’    }
 699â†’
 700â†’    if (sanitizePath === 'help') {
 701â†’      const project = parsedUrl.searchParams.get('project');
 702â†’      const target = parsedUrl.searchParams.get('target');
 703â†’
 704â†’      try {
 705â†’        const text = getHelpTextFromTarget(project, target);
 706â†’        res.writeHead(200, { 'Content-Type': 'application/javascript' });
 707â†’        res.end(JSON.stringify({ text, success: true }));
 708â†’      } catch (err) {
 709â†’        res.writeHead(200, { 'Content-Type': 'application/javascript' });
 710â†’        res.end(JSON.stringify({ text: err.message, success: false }));
 711â†’      }
 712â†’      return;
 713â†’    }
 714â†’
 715â†’    let pathname = join(__dirname, '../../core/graph/', sanitizePath);
 716â†’    // if the file is not found or is a directory, return index.html
 717â†’    if (!existsSync(pathname) || statSync(pathname).isDirectory()) {
 718â†’      res.writeHead(200, { 'Content-Type': 'text/html' });
 719â†’      res.end(html);
 720â†’      return;
 721â†’    }
 722â†’
 723â†’    try {
 724â†’      const data = readFileSync(pathname);
 725â†’
 726â†’      const ext = parse(pathname).ext;
