import {
  getCheckedProjectItems,
  getDeselectAllButton,
  getSelectAllButton,
  getSelectTargetDropdown,
  getSelectTasksMessage,
  getUncheckedProjectItems,
} from '../support/app.po';

describe('dev mode - task graph with colon project names', () => {
  before(() => {
    cy.intercept('/assets/project-graphs/e2e.json', {
      fixture: 'colon-project-graph.json',
    }).as('getGraph');

    cy.intercept('/assets/task-graphs/e2e.json', {
      fixture: 'colon-project-task-graphs.json',
    }).as('getTaskGraph');

    // Intercept the new lazy loading endpoint
    cy.intercept('GET', '/task-graph.json*', (req) => {
      const url = new URL(req.url);
      const project = url.searchParams.get('project');
      const target = url.searchParams.get('target');
      const configuration = url.searchParams.get('configuration');

      // Create task ID for response
      let taskId = `${project}:${target}`;
      if (configuration) {
        taskId += `:${configuration}`;
      }

      // Load the appropriate fixture data
      cy.fixture('colon-project-task-graphs.json').then((taskGraphs) => {
        const response = {
          taskGraphs: {
            [taskId]: taskGraphs.taskGraphs[taskId]
          },
          plans: {},
          errors: {}
        };
        req.reply(response);
      });
    }).as('getLazyTaskGraph');

    cy.visit('/e2e/tasks');

    // wait for initial graphs to finish loading
    cy.wait('@getGraph');
    cy.wait('@getTaskGraph');
  });

  afterEach(() => {
    // clean up by hiding all tasks
    getDeselectAllButton().click();
    getCheckedProjectItems().should('have.length', 0);
  });

  describe('project names with colons', () => {
    it('should display projects with colon names correctly', () => {
      // Check that projects with colons are displayed
      cy.contains('io.quarkus:quarkus-core').should('be.visible');
      cy.contains('com.example:example-service').should('be.visible');
    });

    it('should handle task selection for projects with colons', () => {
      // Select a project with colons in the name
      cy.get('[data-project="io.quarkus:quarkus-core"]').should('be.visible');
      cy.get('[data-project="io.quarkus:quarkus-core"]').click({ force: true });

      // Verify the project is selected
      cy.get('[data-project="io.quarkus:quarkus-core"][data-active="true"]').should('exist');

      // Verify the select tasks message is hidden
      getSelectTasksMessage().should('not.exist');
    });

    it('should handle target selection for projects with colons', () => {
      // Select buildnumber target which has configuration
      getSelectTargetDropdown().select('buildnumber', { force: true });

      // Verify URL contains the target
      cy.url().should('contain', 'tasks/buildnumber');

      // Select a project with colons
      cy.get('[data-project="io.quarkus:quarkus-core"]').click({ force: true });

      // The lazy loading should be triggered
      cy.wait('@getLazyTaskGraph');
    });

    it('should handle configuration-based tasks for colon projects', () => {
      // Select buildnumber target with configuration
      getSelectTargetDropdown().select('buildnumber', { force: true });

      // Select the project
      cy.get('[data-project="io.quarkus:quarkus-core"]').click({ force: true });

