      // Select tasks message should be visible again
      getSelectTasksMessage().should('be.visible');
    });
  });

  describe('task graph API with colon projects', () => {
    it('should make correct API calls for projects with colons', () => {
      // Select a target
      getSelectTargetDropdown().select('build', { force: true });

      // Select a project with colons
