      // Verify the API call is made with correct parameters
      cy.wait('@getLazyTaskGraph').then((interception) => {
        const url = new URL(interception.request.url);
        expect(url.searchParams.get('project')).to.equal('io.quarkus:quarkus-core');
        expect(url.searchParams.get('target')).to.equal('build');
        expect(url.searchParams.get('configuration')).to.be.null;
      });
    });

    it('should make correct API calls for tasks with configuration', () => {
      // Select a target with configuration
      getSelectTargetDropdown().select('buildnumber', { force: true });

      // Select a project with colons
      cy.get('[data-project="io.quarkus:quarkus-core"]').click({ force: true });

      // Verify the API call is made with correct parameters including configuration
      cy.wait('@getLazyTaskGraph').then((interception) => {
        const url = new URL(interception.request.url);
        expect(url.searchParams.get('project')).to.equal('io.quarkus:quarkus-core');
        expect(url.searchParams.get('target')).to.equal('buildnumber');
        // Configuration might be passed separately or as part of the task resolution
      });
    });
  });
