 115â†’function buildEnvironmentJs(
 116â†’  exclude: string[],
 117â†’  watchMode: boolean,
 118â†’  localMode: 'build' | 'serve',
 119â†’  depGraphClientResponse?: ProjectGraphClientResponse,
 120â†’  taskGraphClientResponse?: TaskGraphClientResponse,
 121â†’  expandedTaskInputsReponse?: ExpandedTaskInputsReponse,
 122â†’  sourceMapsResponse?: ConfigurationSourceMaps
 123â†’) {
 124â†’  let environmentJs = `window.exclude = ${JSON.stringify(exclude)};
 125â†’  window.watch = ${!!watchMode};
 126â†’  window.environment = 'release';
 127â†’  window.localMode = '${localMode}';
 128â†’
 129â†’  window.appConfig = {
 130â†’    showDebugger: false,
 131â†’    showExperimentalFeatures: false,
 132â†’    workspaces: [
 133â†’      {
 134â†’        id: 'local',
 135â†’        label: 'local',
 136â†’        projectGraphUrl: 'project-graph.json',
 137â†’        taskGraphUrl: 'task-graph.json',
 138â†’        taskInputsUrl: 'task-inputs.json',
 139â†’        sourceMapsUrl: 'source-maps.json'
 140â†’      }
 141â†’    ],
 142â†’    defaultWorkspaceId: 'local',
 143â†’  };
 144â†’  `;
 145â†’
 146â†’  if (localMode === 'build') {
 147â†’    environmentJs += `window.projectGraphResponse = ${JSON.stringify(
 148â†’      depGraphClientResponse
 149â†’    )};
 150â†’    `;
 151â†’
 152â†’    environmentJs += `window.taskGraphResponse = ${JSON.stringify(
 153â†’      taskGraphClientResponse
 154â†’    )};
 155â†’    `;
 156â†’    environmentJs += `window.expandedTaskInputsResponse = ${JSON.stringify(
 157â†’      expandedTaskInputsReponse
 158â†’    )};`;
 159â†’
 160â†’    environmentJs += `window.sourceMapsResponse = ${JSON.stringify(
 161â†’      sourceMapsResponse
 162â†’    )};`;
 163â†’  } else {
 164â†’    environmentJs += `window.projectGraphResponse = null;`;
 165â†’    environmentJs += `window.taskGraphResponse = null;`;
 166â†’    environmentJs += `window.expandedTaskInputsResponse = null;`;
 167â†’    environmentJs += `window.sourceMapsResponse = null;`;
 168â†’  }
 169â†’
