 173â†’function projectExists(
 174â†’  projects: ProjectGraphProjectNode[],
 175â†’  projectToFind: string
 176â†’) {
 177â†’  return (
 178â†’    projects.find((project) => project.name === projectToFind) !== undefined
 179â†’  );
 180â†’}
