 333â†’
 334â†’  const projects = Object.values(
 335â†’    prunedGraph.nodes
 336â†’  ) as ProjectGraphProjectNode[];
 337â†’  projects.sort((a, b) => {
 338â†’    return a.name.localeCompare(b.name);
 339â†’  });
 340â†’
 341â†’  if (args.focus) {
 342â†’    if (!projectExists(projects, args.focus)) {
 343â†’      output.error({
