 763â†’let currentProjectGraphClientResponse: ProjectGraphClientResponse = {
 764â†’  hash: null,
 765â†’  projects: [],
 766â†’  dependencies: {},
 767â†’  fileMap: {},
 768â†’  layout: {
 769â†’    appsDir: '',
