 781â†’function debounce(fn: (...args) => void, time: number) {
 782â†’  let timeout: NodeJS.Timeout;
 783â†’
 784â†’  return (...args) => {
 785â†’    if (timeout) {
 786â†’      clearTimeout(timeout);
 787â†’    }
 788â†’
 789â†’    timeout = setTimeout(() => fn(...args), time);
 790â†’  };
 791â†’}
