 947â†’async function createTaskGraphClientResponse(
 948â†’  pruneExternal: boolean = false
 949â†’): Promise<TaskGraphClientResponse> {
 950â†’  let graph: ProjectGraph;
 951â†’  try {
 952â†’    graph = await createProjectGraphAsync({ exitOnError: false });
 953â†’  } catch (e) {
 954â†’    if (e instanceof ProjectGraphError) {
 955â†’      graph = e.getPartialProjectGraph();
 956â†’    }
 957â†’  }
 958â†’  if (pruneExternal) {
 959â†’    graph = pruneExternalNodes(graph);
 960â†’  }
 961â†’
 962â†’  const nxJson = readNxJson();
 963â†’
 964â†’  performance.mark('task graph generation:start');
 965â†’  const taskGraphs = getAllTaskGraphsForWorkspace(graph);
 966â†’  performance.mark('task graph generation:end');
 967â†’
 968â†’  const planner = new HashPlanner(
 969â†’    nxJson,
 970â†’    transferProjectGraph(transformProjectGraphForRust(graph))
 971â†’  );
 972â†’  performance.mark('task hash plan generation:start');
 973â†’  const plans: Record<string, string[]> = {};
 974â†’  for (const individualTaskGraph of Object.values(taskGraphs.taskGraphs)) {
 975â†’    for (const task of Object.values(individualTaskGraph.tasks)) {
 976â†’      if (plans[task.id]) {
 977â†’        continue;
 978â†’      }
 979â†’
 980â†’      plans[task.id] = planner.getPlans([task.id], individualTaskGraph)[
 981â†’        task.id
 982â†’      ];
 983â†’    }
 984â†’  }
 985â†’  performance.mark('task hash plan generation:end');
 986â†’
 987â†’  performance.measure(
 988â†’    'task graph generation',
 989â†’    'task graph generation:start',
 990â†’    'task graph generation:end'
 991â†’  );
 992â†’
 993â†’  performance.measure(
