function getAllTaskGraphsForWorkspace(projectGraph: ProjectGraph): {
  taskGraphs: Record<string, TaskGraph>;
  errors: Record<string, string>;
} {
  const taskGraphs: Record<string, TaskGraph> = {};
  const taskGraphErrors: Record<string, string> = {};

  // TODO(cammisuli): improve performance here. Cache results or something.
  for (const projectName in projectGraph.nodes) {
    const project = projectGraph.nodes[projectName];
    const targets = Object.keys(project.data.targets ?? {});

    targets.forEach((target) => {
      const taskId = createTaskId(projectName, target);
      try {
        taskGraphs[taskId] = createTaskGraph(
          projectGraph,
          {},
          [projectName],
          [target],
          undefined,
          {}
        );
      } catch (err) {
        taskGraphs[taskId] = {
          tasks: {},
          dependencies: {},
          continuousDependencies: {},
          roots: [],
        };

        taskGraphErrors[taskId] = err.message;
      }

      const configurations = Object.keys(
        project.data.targets[target]?.configurations || {}
      );

      if (configurations.length > 0) {
        configurations.forEach((configuration) => {
          const taskId = createTaskId(projectName, target, configuration);
          try {
            taskGraphs[taskId] = createTaskGraph(
              projectGraph,
              {},
              [projectName],
              [target],
              configuration,
              {}
            );
          } catch (err) {
            taskGraphs[taskId] = {
              tasks: {},
              dependencies: {},
              continuousDependencies: {},
              roots: [],
            };

            taskGraphErrors[taskId] = err.message;
          }
        });
      }
    });
  }

  return { taskGraphs, errors: taskGraphErrors };
}

function createTaskId(
  projectId: string,
  targetId: string,
  configurationId?: string
) {
  if (configurationId) {
    return `${projectId}:${targetId}:${configurationId}`;
  } else {
    return `${projectId}:${targetId}`;
  }
}

function parseTaskId(taskId: string): {
  projectName: string;
  targetName: string;
  configuration?: string;
} {
  const parts = taskId.split(':');

  if (parts.length < 2) {
    throw new Error(`Invalid task ID format: ${taskId}`);
  }

  if (parts.length === 2) {
    // No configuration: projectName:targetName
    return {
      projectName: parts[0],
      targetName: parts[1],
    };
  } else {
    // With configuration: projectName:targetName:configuration
    // Since project names can contain colons, we parse from the end
    const configuration = parts[parts.length - 1];
    const targetName = parts[parts.length - 2];
    const projectName = parts.slice(0, -2).join(':');

    return {
      projectName,
      targetName,
      configuration,
    };
  }
}

// Performance optimized functions for lazy loading task graphs

// In-memory cache for task graphs to avoid regeneration
const taskGraphCache = new Map<string, TaskGraphClientResponse>();
let taskGraphMetadataCache: {
  projects: Array<{
    name: string;
    targets: Array<{ name: string; configurations?: string[] }>;
  }>;
} | null = null;

// Clear cache when project graph changes
function clearTaskGraphCache() {
  taskGraphCache.clear();
  taskGraphMetadataCache = null;
}

/**
 * Creates a lightweight metadata response containing only project names and targets
 * This allows the UI to build the task selection tree without loading all task graphs
 */
async function createTaskGraphMetadataResponse(): Promise<{
  projects: Array<{
    name: string;
    targets: Array<{
      name: string;
      configurations?: string[];
    }>;
  }>;
}> {
  // Check cache first
  if (taskGraphMetadataCache) {
    return taskGraphMetadataCache;
  }

  let graph: ProjectGraph;
  try {
    graph = await createProjectGraphAsync({ exitOnError: false });
  } catch (e) {
    if (e instanceof ProjectGraphError) {
      graph = e.getPartialProjectGraph();
    }
  }

  const projects = Object.values(graph.nodes).map((project) => ({
    name: project.name,
    targets: Object.entries(project.data.targets ?? {}).map(
      ([targetName, target]) => ({
        name: targetName,
        configurations: target.configurations
          ? Object.keys(target.configurations)
          : undefined,
      })
    ),
  }));

  // Cache the result
  taskGraphMetadataCache = { projects };
  return taskGraphMetadataCache;
}

/**
 * Creates a task graph response for a specific project and target
 * This is much faster than generating all task graphs upfront
 */
async function createSpecificTaskGraphResponse(
  projectName: string,
  targetName: string,
  configuration?: string
): Promise<TaskGraphClientResponse> {
  const taskId = createTaskId(projectName, targetName, configuration);

  // Check cache first
  const cached = taskGraphCache.get(taskId);
  if (cached) {
    return cached;
  }

  let graph: ProjectGraph;
  try {
    graph = await createProjectGraphAsync({ exitOnError: false });
  } catch (e) {
    if (e instanceof ProjectGraphError) {
      graph = e.getPartialProjectGraph();
    }
  }

  const nxJson = readNxJson();

  performance.mark(`specific task graph generation:start`);

  // Create only the specific task graph requested
  const taskGraphs: Record<string, TaskGraph> = {};
  const taskGraphErrors: Record<string, string> = {};

  try {
    taskGraphs[taskId] = createTaskGraph(
      graph,
      {},
      [projectName],
      [targetName],
      configuration,
      {}
    );
  } catch (err) {
    taskGraphs[taskId] = {
      tasks: {},
      dependencies: {},
      continuousDependencies: {},
      roots: [],
    };
    taskGraphErrors[taskId] = err.message;
  }

  performance.mark(`specific task graph generation:end`);

  const planner = new HashPlanner(
    nxJson,
    transferProjectGraph(transformProjectGraphForRust(graph))
  );

  performance.mark('specific task hash plan generation:start');
  const plans: Record<string, string[]> = {};

  // Get plans for all tasks in the task graph
  const taskIds = Object.keys(taskGraphs[taskId].tasks);
  const taskPlans = planner.getPlans(taskIds, taskGraphs[taskId]);
  Object.assign(plans, taskPlans);

  performance.mark('specific task hash plan generation:end');

  const result = {
    taskGraphs,
    plans,
    errors: taskGraphErrors,
  };

  // Cache the result
  taskGraphCache.set(taskId, result);

  performance.measure(
    `specific task graph generation for ${taskId}`,
    `specific task graph generation:start`,
    `specific task graph generation:end`
  );

  performance.measure(
    'specific task hash plan generation',
    'specific task hash plan generation:start',
    'specific task hash plan generation:end'
  );

  return result;
}

async function getExpandedTaskInputs(
  projectName: string,
  targetName: string,
  configuration?: string
): Promise<Record<string, string[]>> {
  const taskGraphResponse = await createSpecificTaskGraphResponse(
    projectName,
    targetName,
    configuration
  );

  const taskId = createTaskId(projectName, targetName, configuration);

  const allWorkspaceFiles = await allFileData();

  const inputs = taskGraphResponse.plans[taskId];
  if (inputs) {
    return expandInputs(
      inputs,
      currentProjectGraphClientResponse.projects.find(
        (p) => p.name === projectName
      ),
      allWorkspaceFiles,
      currentProjectGraphClientResponse
    );
  }
  return {};
}

function expandInputs(
  inputs: string[],
  project: ProjectGraphProjectNode,
  allWorkspaceFiles: FileData[],
  depGraphClientResponse: ProjectGraphClientResponse
): Record<string, string[]> {
  const projectNames = depGraphClientResponse.projects.map((p) => p.name);

  const workspaceRootInputs: string[] = [];
  const projectRootInputs: string[] = [];
  const externalInputs: string[] = [];
  const otherInputs: string[] = [];
  inputs.forEach((input) => {
    // grouped workspace inputs look like workspace:[pattern,otherPattern]
    if (input.startsWith('workspace:[')) {
      const inputs = input.substring(11, input.length - 1).split(',');
      workspaceRootInputs.push(...inputs);
      return;
    }
    const maybeProjectName = input.split(':')[0];
    if (projectNames.includes(maybeProjectName)) {
      projectRootInputs.push(input);
      return;
    }
    if (
      input === 'ProjectConfiguration' ||
      input === 'TsConfig' ||
      input === 'AllExternalDependencies'
    ) {
      otherInputs.push(input);
      return;
    }
    // there shouldn't be any other imports in here, but external ones are always going to have a modifier in front
    if (input.includes(':')) {
      externalInputs.push(input);
      return;
    }
  });

  const workspaceRootsExpanded: string[] = getExpandedWorkspaceRoots(
    workspaceRootInputs,
    allWorkspaceFiles
  );

  const otherInputsExpanded = otherInputs.map((input) => {
    if (input === 'TsConfig') {
      return relative(workspaceRoot, getRootTsConfigPath());
    }
    if (input === 'ProjectConfiguration') {
      return depGraphClientResponse.fileMap[project.name].find(
        (file) =>
          file.file === `${project.data.root}/project.json` ||
          file.file === `${project.data.root}/package.json`
      ).file;
    }

    return input;
  });

  const projectRootsExpanded = projectRootInputs
    .map((input) => {
      const fileSetProjectName = input.split(':')[0];
      const fileSetProject = depGraphClientResponse.projects.find(
        (p) => p.name === fileSetProjectName
      );
      const fileSets = input.replace(`${fileSetProjectName}:`, '').split(',');

      const projectInputExpanded = {
        [fileSetProject.name]: filterUsingGlobPatterns(
          fileSetProject.data.root,
          depGraphClientResponse.fileMap[fileSetProject.name],
          fileSets
        ).map((f) => f.file),
      };

      return projectInputExpanded;
    })
    .reduce((curr, acc) => {
      for (let key in curr) {
        acc[key] = curr[key];
      }
      return acc;
    }, {});

  return {
    general: [...workspaceRootsExpanded, ...otherInputsExpanded],
    ...projectRootsExpanded,
    external: externalInputs,
  };
}

/**
 * The data type that `nx graph --file graph.json` or `nx build --graph graph.json` contains
 */
export interface GraphJson {
  /**
   * A graph of tasks populated with `nx build --graph`
   */
  tasks?: TaskGraph;
  /**
   * The plans for hashing a task in the task graph
   */
  taskPlans?: Record<string, string[]>;
  /**
   * The project graph
   */
  graph: ProjectGraph;
}

function getExpandedWorkspaceRoots(
  workspaceRootInputs: string[],
  allWorkspaceFiles: FileData[]
) {
  const workspaceRootsExpanded: string[] = [];
  const negativeWRPatterns = [];
  const positiveWRPatterns = [];
  for (const fileset of workspaceRootInputs) {
    if (fileset.startsWith('!')) {
      negativeWRPatterns.push(fileset.substring(17));
    } else {
      positiveWRPatterns.push(fileset.substring(16));
    }
  }
  for (const pattern of positiveWRPatterns) {
    const matchingFile = allWorkspaceFiles.find((t) => t.file === pattern);
    if (
      matchingFile &&
      !negativeWRPatterns.some((p) => minimatch(matchingFile.file, p))
    ) {
      workspaceRootsExpanded.push(matchingFile.file);
    } else {
      allWorkspaceFiles
        .filter(
          (f) =>
            minimatch(f.file, pattern) &&
            !negativeWRPatterns.some((p) => minimatch(f.file, p))
        )
        .forEach((f) => {
          workspaceRootsExpanded.push(f.file);
        });
    }
  }
  workspaceRootsExpanded.sort();
  return workspaceRootsExpanded;
}

async function createJsonOutput(
  prunedGraph: ProjectGraph,
  rawGraph: ProjectGraph,
  projects: string[],
  targets?: string[]
): Promise<GraphJson> {
  const response: GraphJson = {
    graph: prunedGraph,
  };

  if (targets?.length) {
    const taskGraph = createTaskGraph(
      rawGraph,
      {},
      projects,
      targets,
      undefined,
      {}
    );

    const hasher = createTaskHasher(rawGraph, readNxJson());
    let tasks = Object.values(taskGraph.tasks);
    const hashes = await hasher.hashTasks(tasks, taskGraph);
    response.tasks = taskGraph;
    response.taskPlans = tasks.reduce((acc, task, index) => {
      acc[task.id] = Object.keys(hashes[index].details.nodes).sort();
      return acc;
    }, {});
  }

  return response;
}

function getHelpTextFromTarget(
  projectName: string,
  targetName: string
): string {
  if (!projectName) throw new Error(`Missing project`);
  if (!targetName) throw new Error(`Missing target`);

  const project = currentProjectGraphClientResponse.projects?.find(
    (p) => p.name === projectName
  );
  if (!project) throw new Error(`Cannot find project ${projectName}`);

  const target = project.data.targets[targetName];
  if (!target) throw new Error(`Cannot find target ${targetName}`);

  const command = target.metadata?.help?.command;
  if (!command)
    throw new Error(`No help command found for ${projectName}:${targetName}`);

  return execSync(command, {
    cwd: target.options?.cwd ?? workspaceRoot,
    windowsHide: false,
  }).toString();
}

