  1	import { runCommand } from '../../tasks-runner/run-command';
  2	import {
  3	  readGraphFileFromGraphArg,
  4	  splitArgsIntoNxArgsAndOverrides,
  5	} from '../../utils/command-line-utils';
  6	import { connectToNxCloudIfExplicitlyAsked } from '../connect/connect-to-nx-cloud';
  7	import { performance } from 'perf_hooks';
  8	import {
  9	  createProjectGraphAsync,
 10	  readProjectsConfigurationFromProjectGraph,
 11	} from '../../project-graph/project-graph';
 12	import {
 13	  ProjectGraph,
 14	  ProjectGraphProjectNode,
 15	} from '../../config/project-graph';
 16	import { NxJsonConfiguration } from '../../config/nx-json';
 17	import { workspaceRoot } from '../../utils/workspace-root';
 18	import { splitTarget } from '../../utils/split-target';
 19	import { output } from '../../utils/output';
 20	import { TargetDependencyConfig } from '../../config/workspace-json-project-json';
 21	import { readNxJson } from '../../config/configuration';
 22	import { calculateDefaultProjectName } from '../../config/calculate-default-project-name';
 23	import { generateGraph } from '../graph/graph';
 24	import { findMatchingProjects } from '../../utils/find-matching-projects';
 25	
 26	export async function runOne(
 27	  cwd: string,
 28	  args: { [k: string]: any },
 29	  extraTargetDependencies: Record<
 30	    string,
 31	    (TargetDependencyConfig | string)[]
 32	  > = {},
 33	  extraOptions = {
 34	    excludeTaskDependencies: args.excludeTaskDependencies,
 35	    loadDotEnvFiles: process.env.NX_LOAD_DOT_ENV_FILES !== 'false',
 36	  } as {
 37	    excludeTaskDependencies: boolean;
 38	    loadDotEnvFiles: boolean;
 39	  }
 40	): Promise<void> {
 41	  performance.mark('code-loading:end');
 42	  performance.measure('code-loading', 'init-local', 'code-loading:end');
 43	
 44	  const nxJson = readNxJson();
 45	  const projectGraph = await createProjectGraphAsync();
 46	
 47	  const opts = parseRunOneOptions(cwd, args, projectGraph, nxJson);
 48	
 49	  const { nxArgs, overrides } = splitArgsIntoNxArgsAndOverrides(
 50	    {
 51	      ...opts.parsedArgs,
 52	      configuration: opts.configuration,
 53	      targets: [opts.target],
 54	    },
 55	    'run-one',
 56	    { printWarnings: args.graph !== 'stdout' },
 57	    nxJson
 58	  );
 59	
 60	  if (nxArgs.help) {
 61	    await (await import('./run')).printTargetRunHelp(opts, workspaceRoot);
 62	    process.exit(0);
 63	  }
 64	
 65	  await connectToNxCloudIfExplicitlyAsked(nxArgs);
 66	
 67	  const { projects, projectName } = getProjects(projectGraph, opts.project);
 68	
 69	  if (nxArgs.graph) {
 70	    const projectNames = projects.map((t) => t.name);
 71	    const file = readGraphFileFromGraphArg(nxArgs);
 72	
 73	    return await generateGraph(
 74	      {
 75	        watch: true,
 76	        open: true,
 77	        view: 'tasks',
 78	        targets: nxArgs.targets,
 79	        projects: projectNames,
 80	        file,
 81	      },
 82	      projectNames
 83	    );
 84	  } else {
 85	    const status = await runCommand(
 86	      projects,
 87	      projectGraph,
 88	      { nxJson },
 89	      nxArgs,
 90	      overrides,
 91	      projectName,
 92	      extraTargetDependencies,
 93	      extraOptions
 94	    );
 95	    process.exit(status);
 96	  }
 97	}
 98	
 99	function getProjects(
100	  projectGraph: ProjectGraph,
101	  projectName: string
102	): {
103	  projectName: string;
104	  projects: ProjectGraphProjectNode[];
105	  projectsMap: Record<string, ProjectGraphProjectNode>;
106	} {
107	  if (projectGraph.nodes[projectName]) {
108	    return {
109	      projectName: projectName,
110	      projects: [projectGraph.nodes[projectName]],
111	      projectsMap: {
112	        [projectName]: projectGraph.nodes[projectName],
113	      },
114	    };
115	  } else {
116	    const projects = findMatchingProjects([projectName], projectGraph.nodes);
117	    if (projects.length === 1) {
118	      const projectName = projects[0];
119	      const project = projectGraph.nodes[projectName];
120	      return {
121	        projectName,
122	        projects: [project],
123	        projectsMap: {
124	          [project.data.name]: project,
125	        },
126	      };
127	    } else if (projects.length > 1) {
128	      output.error({
129	        title: `Multiple projects matched:`,
130	        bodyLines:
131	          projects.length > 100 ? [...projects.slice(0, 100), '...'] : projects,
132	      });
133	      process.exit(1);
134	    }
135	  }
136	
137	  output.error({
138	    title: `Cannot find project '${projectName}'`,
139	  });
140	  process.exit(1);
141	}
142	
143	const targetAliases = {
144	  b: 'build',
145	  e: 'e2e',
146	  l: 'lint',
147	  s: 'serve',
148	  t: 'test',
149	};
150	
151	function parseRunOneOptions(
152	  cwd: string,
153	  parsedArgs: { [k: string]: any },
154	  projectGraph: ProjectGraph,
155	  nxJson: NxJsonConfiguration
156	): { project; target; configuration; parsedArgs } {
157	  const defaultProjectName = calculateDefaultProjectName(
158	    cwd,
159	    workspaceRoot,
160	    readProjectsConfigurationFromProjectGraph(projectGraph),
161	    nxJson
162	  );
163	
164	  let project;
165	  let target;
166	  let configuration;
167	
168	  if (parsedArgs['project:target:configuration']?.indexOf(':') > -1) {
169	    // run case
170	    [project, target, configuration] = splitTarget(
171	      parsedArgs['project:target:configuration'],
172	      projectGraph
173	    );
174	    // this is to account for "nx npmsript:dev"
