import { parseRunOneOptions } from './run-one';
import { ProjectGraph } from '../../config/project-graph';
import { NxJsonConfiguration } from '../../config/nx-json';

// Mock the module to access the private function
jest.mock('./run-one', () => {
  const actual = jest.requireActual('./run-one');
  return {
    ...actual,
    parseRunOneOptions: actual.parseRunOneOptions || jest.fn(),
  };
});

describe('parseRunOneOptions', () => {
  let projectGraph: ProjectGraph;
  let nxJson: NxJsonConfiguration;

  beforeEach(() => {
    nxJson = {};
    projectGraph = {
      nodes: {
        'app-with-run': {
          name: 'app-with-run',
          type: 'app',
          data: {
            root: 'apps/app-with-run',
            targets: {
              run: {
                executor: '@nx/node:execute',
              },
              build: {
                executor: '@nx/webpack:webpack',
              },
            },
          },
        },
        'app-without-run': {
          name: 'app-without-run',
          type: 'app',
          data: {
            root: 'apps/app-without-run',
            targets: {
              serve: {
                executor: '@nx/webpack:dev-server',
              },
              build: {
                executor: '@nx/webpack:webpack',
              },
            },
          },
        },
      },
      dependencies: {},
    } as any;
  });

  describe('default run target behavior', () => {
    it('should default to "run" target when project has a run target and no target is specified', () => {
      const parsedArgs = {
        'project:target:configuration': 'app-with-run',
      };

      const result = parseRunOneOptions('/workspace', parsedArgs, projectGraph, nxJson);

      expect(result.project).toBe('app-with-run');
      expect(result.target).toBe('run');
    });

    it('should not default to "run" target when project does not have a run target', () => {
      const parsedArgs = {
        'project:target:configuration': 'app-without-run',
      };

      expect(() =>
        parseRunOneOptions('/workspace', parsedArgs, projectGraph, nxJson)
      ).toThrow('Both project and target have to be specified');
    });

    it('should respect explicitly specified target even if project has run target', () => {
      const parsedArgs = {
        'project:target:configuration': 'app-with-run:build',
      };

      const result = parseRunOneOptions('/workspace', parsedArgs, projectGraph, nxJson);

      expect(result.project).toBe('app-with-run');
      expect(result.target).toBe('build');
    });

    it('should work with separate project argument and default to run target', () => {
      const parsedArgs = {
        project: 'app-with-run',
      };

      const result = parseRunOneOptions('/workspace', parsedArgs, projectGraph, nxJson);

      expect(result.project).toBe('app-with-run');
      expect(result.target).toBe('run');
    });

    it('should work with project and target specified separately', () => {
      const parsedArgs = {
        project: 'app-with-run',
        target: 'build',
      };

      const result = parseRunOneOptions('/workspace', parsedArgs, projectGraph, nxJson);

      expect(result.project).toBe('app-with-run');
      expect(result.target).toBe('build');
    });
  });
});

