       it('should be able to run targets with unspecified executor given an appropriate targetDefaults entry', () => {
         const target = uniq('target');
diff --git a/packages/nx/src/command-line/run/run-one.ts b/packages/nx/src/command-line/run/run-one.ts
index dc12dd52a9..e2be59de55 100644
--- a/packages/nx/src/command-line/run/run-one.ts
+++ b/packages/nx/src/command-line/run/run-one.ts
@@ -185,6 +185,15 @@ function parseRunOneOptions(
   if (!project && defaultProjectName) {
     project = defaultProjectName;
   }
+
+  // If project is specified but no target, check if the project has a "run" target
+  if (project && !target) {
+    const projectNode = projectGraph.nodes[project];
+    if (projectNode?.data?.targets?.['run']) {
+      target = 'run';
+    }
+  }
+
   if (!project || !target) {
     throw new Error(`Both project and target have to be specified`);
   }

