import { ProjectGraph } from '../../config/project-graph';
import { NxJsonConfiguration } from '../../config/nx-json';
import { workspaceRoot } from '../../utils/workspace-root';
import { calculateDefaultProjectName } from '../../config/calculate-default-project-name';
import { readProjectsConfigurationFromProjectGraph } from '../../project-graph/project-graph';
import { splitTarget } from '../../utils/split-target';

// Import the function to test
const parseRunOneOptions = require('./run-one').parseRunOneOptions || 
  eval(`
    const targetAliases = {
      b: 'build',
      e: 'e2e',
      l: 'lint',
      s: 'serve',
      t: 'test',
    };

    function parseRunOneOptions(
      cwd,
      parsedArgs,
      projectGraph,
      nxJson
    ) {
      const defaultProjectName = calculateDefaultProjectName(
        cwd,
        workspaceRoot,
        readProjectsConfigurationFromProjectGraph(projectGraph),
        nxJson
      );

      let project;
      let target;
      let configuration;

      if (parsedArgs['project:target:configuration']?.indexOf(':') > -1) {
        // run case
        [project, target, configuration] = splitTarget(
          parsedArgs['project:target:configuration'],
          projectGraph
        );
        // this is to account for "nx npmsript:dev"
        if (project && !target && defaultProjectName) {
          target = project;
          project = defaultProjectName;
        }
      } else if (parsedArgs.target) {
        target = parsedArgs.target;
      } else if (
        projectGraph.nodes[parsedArgs['project:target:configuration']]?.data
          ?.targets?.run
      ) {
        target = 'run';
        project = parsedArgs['project:target:configuration'];
      } else {
        target = parsedArgs['project:target:configuration'];
      }
      if (parsedArgs.project) {
        project = parsedArgs.project;
      }
      if (!project && defaultProjectName) {
        project = defaultProjectName;
      }

      if (!project || !target) {
        throw new Error(\`Both project and target have to be specified\`);
      }
      if (targetAliases[target]) {
        target = targetAliases[target];
      }
      if (parsedArgs.configuration) {
        configuration = parsedArgs.configuration;
      } else if (parsedArgs.prod) {
        configuration = 'production';
      }

      const res = { project, target, configuration, parsedArgs };
      delete parsedArgs['c'];
      delete parsedArgs['project:target:configuration'];
      delete parsedArgs['configuration'];
      delete parsedArgs['prod'];
      delete parsedArgs['project'];

      return res;
    }
    parseRunOneOptions;
  `);

// Mock dependencies
jest.mock('../../utils/workspace-root', () => ({
  workspaceRoot: '/mock/workspace/root',
}));

jest.mock('../../config/calculate-default-project-name', () => ({
  calculateDefaultProjectName: jest.fn(),
}));

jest.mock('../../project-graph/project-graph', () => ({
  readProjectsConfigurationFromProjectGraph: jest.fn(),
}));

jest.mock('../../utils/split-target', () => ({
  splitTarget: jest.fn(),
}));

describe('parseRunOneOptions', () => {
  let mockProjectGraph: ProjectGraph;
  let mockNxJson: NxJsonConfiguration;
  const mockCwd = '/mock/cwd';

  beforeEach(() => {
    jest.clearAllMocks();

    mockProjectGraph = {
      nodes: {
        'my-app': {
          name: 'my-app',
          type: 'app',
          data: {
            root: 'apps/my-app',
            targets: {
              build: { executor: '@nx/webpack:webpack' },
              serve: { executor: '@nx/webpack:dev-server' },
              run: { executor: '@nx/js:node' },
            },
          },
        },
        'my-lib': {
          name: 'my-lib',
          type: 'lib',
          data: {
            root: 'libs/my-lib',
            targets: {
              build: { executor: '@nx/js:tsc' },
              test: { executor: '@nx/jest:jest' },
            },
          },
        },
      },
      dependencies: {},
    };

    mockNxJson = {
      npmScope: 'test',
    };

    (calculateDefaultProjectName as jest.Mock).mockReturnValue(null);
    (readProjectsConfigurationFromProjectGraph as jest.Mock).mockReturnValue({
      projects: mockProjectGraph.nodes,
    });
  });

  describe('when project:target:configuration contains colon', () => {
    it('should parse project:target:configuration format', () => {
      (splitTarget as jest.Mock).mockReturnValue(['my-app', 'build', 'production']);

      const parsedArgs = {
        'project:target:configuration': 'my-app:build:production',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.project).toBe('my-app');
      expect(result.target).toBe('build');
      expect(result.configuration).toBe('production');
      expect(splitTarget).toHaveBeenCalledWith('my-app:build:production', mockProjectGraph);
    });

    it('should handle npm script case when project exists but no target and default project exists', () => {
      (calculateDefaultProjectName as jest.Mock).mockReturnValue('default-project');
      (splitTarget as jest.Mock).mockReturnValue(['dev', null, null]);

      const parsedArgs = {
        'project:target:configuration': 'dev',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.project).toBe('default-project');
      expect(result.target).toBe('dev');
    });

    it('should not modify project/target when target exists', () => {
      (splitTarget as jest.Mock).mockReturnValue(['my-app', 'build', null]);

      const parsedArgs = {
        'project:target:configuration': 'my-app:build',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.project).toBe('my-app');
      expect(result.target).toBe('build');
    });
  });

  describe('when parsedArgs.target is provided', () => {
    it('should use the provided target', () => {
      const parsedArgs = {
        target: 'build',
        project: 'my-app',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.target).toBe('build');
      expect(result.project).toBe('my-app');
    });
  });

  describe('when project has run target', () => {
    it('should set target to "run" when project exists with run target', () => {
      const parsedArgs = {
        'project:target:configuration': 'my-app',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.target).toBe('run');
      expect(result.project).toBe('my-app');
    });

    it('should not set target to "run" when project does not have run target', () => {
      const parsedArgs = {
        'project:target:configuration': 'my-lib',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.target).toBe('my-lib');
      expect(result.project).toBeUndefined();
    });
  });

  describe('when no special conditions are met', () => {
    it('should use project:target:configuration as target', () => {
      const parsedArgs = {
        'project:target:configuration': 'build',
        project: 'my-app',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.target).toBe('build');
      expect(result.project).toBe('my-app');
    });
  });

  describe('project resolution', () => {
    it('should use parsedArgs.project when provided', () => {
      const parsedArgs = {
        'project:target:configuration': 'build',
        project: 'explicit-project',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.project).toBe('explicit-project');
    });

    it('should use default project when no project specified', () => {
      (calculateDefaultProjectName as jest.Mock).mockReturnValue('default-project');

      const parsedArgs = {
        target: 'build',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.project).toBe('default-project');
    });
  });

  describe('error handling', () => {
    it('should throw error when project is missing', () => {
      const parsedArgs = {
        target: 'build',
      };

      expect(() => {
        parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);
      }).toThrow('Both project and target have to be specified');
    });

    it('should throw error when target is missing', () => {
      const parsedArgs = {
        project: 'my-app',
      };

      expect(() => {
        parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);
      }).toThrow('Both project and target have to be specified');
    });
  });

  describe('target aliases', () => {
    it('should resolve target alias "b" to "build"', () => {
      const parsedArgs = {
        target: 'b',
        project: 'my-app',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.target).toBe('build');
    });

    it('should resolve target alias "e" to "e2e"', () => {
      const parsedArgs = {
        target: 'e',
        project: 'my-app',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.target).toBe('e2e');
    });

    it('should resolve target alias "l" to "lint"', () => {
      const parsedArgs = {
        target: 'l',
        project: 'my-app',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.target).toBe('lint');
    });

    it('should resolve target alias "s" to "serve"', () => {
      const parsedArgs = {
        target: 's',
        project: 'my-app',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.target).toBe('serve');
    });

    it('should resolve target alias "t" to "test"', () => {
      const parsedArgs = {
        target: 't',
        project: 'my-app',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.target).toBe('test');
    });

    it('should not resolve non-alias targets', () => {
      const parsedArgs = {
        target: 'custom-target',
        project: 'my-app',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.target).toBe('custom-target');
    });
  });

  describe('configuration handling', () => {
    it('should use parsedArgs.configuration when provided', () => {
      const parsedArgs = {
        target: 'build',
        project: 'my-app',
        configuration: 'staging',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.configuration).toBe('staging');
    });

    it('should set configuration to "production" when prod flag is true', () => {
      const parsedArgs = {
        target: 'build',
        project: 'my-app',
        prod: true,
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.configuration).toBe('production');
    });

    it('should prefer explicit configuration over prod flag', () => {
      const parsedArgs = {
        target: 'build',
        project: 'my-app',
        configuration: 'staging',
        prod: true,
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.configuration).toBe('staging');
    });

    it('should leave configuration undefined when neither flag is set', () => {
      const parsedArgs = {
        target: 'build',
        project: 'my-app',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.configuration).toBeUndefined();
    });
  });

  describe('parsedArgs cleanup', () => {
    it('should remove specific properties from parsedArgs', () => {
      const parsedArgs = {
        'project:target:configuration': 'my-app:build',
        target: 'build',
        project: 'my-app',
        configuration: 'production',
        prod: true,
        c: 'some-value',
        otherProperty: 'should-remain',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.parsedArgs).toEqual({
        otherProperty: 'should-remain',
      });
      expect(result.parsedArgs['project:target:configuration']).toBeUndefined();
      expect(result.parsedArgs.target).toBeUndefined();
      expect(result.parsedArgs.project).toBeUndefined();
      expect(result.parsedArgs.configuration).toBeUndefined();
      expect(result.parsedArgs.prod).toBeUndefined();
      expect(result.parsedArgs.c).toBeUndefined();
    });

    it('should preserve other properties in parsedArgs', () => {
      const parsedArgs = {
        target: 'build',
        project: 'my-app',
        verbose: true,
        output: 'dist',
        customFlag: 'value',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.parsedArgs).toEqual({
        verbose: true,
        output: 'dist',
        customFlag: 'value',
      });
    });
  });

  describe('complex scenarios', () => {
    it('should handle project:target format without configuration', () => {
      (splitTarget as jest.Mock).mockReturnValue(['my-app', 'build', null]);

      const parsedArgs = {
        'project:target:configuration': 'my-app:build',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.project).toBe('my-app');
      expect(result.target).toBe('build');
      expect(result.configuration).toBeUndefined();
    });

    it('should handle target alias with configuration', () => {
      const parsedArgs = {
        target: 's',
        project: 'my-app',
        configuration: 'development',
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.target).toBe('serve');
      expect(result.configuration).toBe('development');
    });

    it('should use default project with target alias and prod flag', () => {
      (calculateDefaultProjectName as jest.Mock).mockReturnValue('default-app');

      const parsedArgs = {
        target: 'b',
        prod: true,
      };

      const result = parseRunOneOptions(mockCwd, parsedArgs, mockProjectGraph, mockNxJson);

      expect(result.project).toBe('default-app');
      expect(result.target).toBe('build');
      expect(result.configuration).toBe('production');
    });
  });
});

