  42	
  43	pub struct App {
  44	    pub components: Vec<Box<dyn Component>>,
  45	    pub quit_at: Option<std::time::Instant>,
  46	    focus: Focus,
  47	    run_mode: RunMode,
  48	    previous_focus: Focus,
  49	    done_callback: Option<ThreadsafeFunction<(), ErrorStrategy::Fatal>>,
  50	    forced_shutdown_callback: Option<ThreadsafeFunction<(), ErrorStrategy::Fatal>>,
  51	    tui_config: TuiConfig,
  52	    // We track whether the user has interacted with the app to determine if we should show perform any auto-exit at all
  53	    user_has_interacted: bool,
  54	    is_forced_shutdown: bool,
  55	    layout_manager: LayoutManager,
  56	    // Cached frame area used for layout calculations, only updated on terminal resize
  57	    frame_area: Option<Rect>,
  58	    // Cached result of layout manager's calculate_layout, only updated when necessary (e.g. terminal resize, task list visibility change etc)
  59	    layout_areas: Option<LayoutAreas>,
  60	    terminal_pane_data: [TerminalPaneData; 2],
  61	    spacebar_mode: bool,
  62	    pane_tasks: [Option<String>; 2], // Tasks assigned to panes 1 and 2 (0-indexed)
  63	    action_tx: Option<UnboundedSender<Action>>,
  64	    resize_debounce_timer: Option<u128>, // Timer for debouncing resize events
  65	    // task id -> pty instance
  66	    pty_instances: HashMap<String, Arc<PtyInstance>>,
  67	    selection_manager: Arc<Mutex<TaskSelectionManager>>,
  68	    pinned_tasks: Vec<String>,
  69	    tasks: Vec<Task>,
  70	    debug_mode: bool,
  71	    debug_state: TuiWidgetState,
  72	    console_messenger: Option<NxConsoleMessageConnection>,
  73	}
  74	
  75	#[derive(Debug, Clone, Copy, PartialEq, Eq)]
  76	pub enum Focus {
  77	    TaskList,
  78	    MultipleOutput(usize),
  79	    HelpPopup,
  80	    CountdownPopup,
  81	}
  82	
  83	impl App {
  84	    pub fn new(
  85	        tasks: Vec<Task>,
  86	        initiating_tasks: HashSet<String>,
  87	        run_mode: RunMode,
  88	        pinned_tasks: Vec<String>,
  89	        tui_config: TuiConfig,
  90	        title_text: String,
  91	    ) -> Result<Self> {
  92	        let task_count = tasks.len();
  93	        let selection_manager = Arc::new(Mutex::new(TaskSelectionManager::new(5)));
  94	
  95	        let initial_focus = Focus::TaskList;
  96	        let tasks_list = TasksList::new(
  97	            tasks.clone(),
  98	            initiating_tasks,
  99	            run_mode,
 100	            initial_focus,
 101	            title_text,
 102	            selection_manager.clone(),
 103	        );
 104	        let help_popup = HelpPopup::new();
 105	        let countdown_popup = CountdownPopup::new();
 106	
 107	        let components: Vec<Box<dyn Component>> = vec![
 108	            Box::new(tasks_list),
 109	            Box::new(help_popup),
 110	            Box::new(countdown_popup),
 111	        ];
 112	
 113	        let main_terminal_pane_data = TerminalPaneData::new();
 114	
 115	        Ok(Self {
 116	            run_mode,
 117	            components,
 118	            pinned_tasks,
 119	            quit_at: None,
 120	            focus: initial_focus,
 121	            previous_focus: Focus::TaskList,
 122	            done_callback: None,
 123	            forced_shutdown_callback: None,
 124	            tui_config,
 125	            user_has_interacted: false,
 126	            is_forced_shutdown: false,
 127	            layout_manager: LayoutManager::new_with_run_mode(task_count, run_mode),
 128	            frame_area: None,
 129	            layout_areas: None,
 130	            terminal_pane_data: [main_terminal_pane_data, TerminalPaneData::new()],
 131	            spacebar_mode: false,
 132	            pane_tasks: [None, None],
 133	            action_tx: None,
 134	            resize_debounce_timer: None,
 135	            pty_instances: HashMap::new(),
 136	            selection_manager,
 137	            tasks,
 138	            debug_mode: false,
 139	            debug_state: TuiWidgetState::default().set_default_display_level(LevelFilter::Debug),
 140	            console_messenger: None,
 141	        })
 142	    }
 143	
 144	    pub fn register_action_handler(&mut self, tx: UnboundedSender<Action>) -> Result<()> {
 145	        self.action_tx = Some(tx);
 146	        Ok(())
 147	    }
 148	
 149	    pub fn init(&mut self, _area: Size) -> Result<()> {
 150	        // Iterate over the pinned tasks and assign them to the terminal panes (up to the maximum of 2), focusing the first one as well
 151	        let pinned_tasks = self.pinned_tasks.clone();
 152	        for (idx, task) in pinned_tasks.iter().enumerate() {
 153	            if idx < 2 {
 154	                self.selection_manager
 155	                    .lock()
 156	                    .unwrap()
 157	                    .select_task(task.clone());
 158	
 159	                if pinned_tasks.len() == 1 && idx == 0 {
 160	                    self.display_and_focus_current_task_in_terminal_pane(match self.run_mode {
 161	                        RunMode::RunMany => true,
 162	                        RunMode::RunOne if self.tasks.len() == 1 => false,
 163	                        RunMode::RunOne => true,
 164	                    });
 165	                } else {
 166	                    self.assign_current_task_to_pane(idx);
 167	                }
 168	            }
 169	        }
 170	        Ok(())
 171	    }
 172	
 173	    pub fn start_command(&mut self, thread_count: Option<u32>) {
 174	        self.dispatch_action(Action::StartCommand(thread_count));
 175	    }
 176	
 177	    pub fn start_tasks(&mut self, tasks: Vec<Task>) {
 178	        self.dispatch_action(Action::StartTasks(tasks));
 179	    }
 180	
 181	    pub fn update_task_status(&mut self, task_id: String, status: TaskStatus) {
 182	        self.dispatch_action(Action::UpdateTaskStatus(task_id.clone(), status));
 183	        if status == TaskStatus::InProgress && self.should_set_interactive_by_default(&task_id) {
 184	            self.terminal_pane_data[0].set_interactive(true);
 185	        }
 186	    }
 187	
 188	    fn should_set_interactive_by_default(&self, task_id: &str) -> bool {
 189	        matches!(self.run_mode, RunMode::RunOne)
 190	            && self
 191	                .pty_instances
 192	                .get(task_id)
 193	                .is_some_and(|pty| pty.can_be_interactive())
 194	    }
 195	
 196	    pub fn print_task_terminal_output(&mut self, task_id: String, output: String) {
 197	        // Tasks run within a pseudo-terminal always have a pty instance and do not need a new one
 198	        // Tasks not run within a pseudo-terminal need a new pty instance to print output
 199	        if !self.pty_instances.contains_key(&task_id) {
 200	            let pty = PtyInstance::non_interactive();
 201	
 202	            // Add ANSI escape sequence to hide cursor at the end of output, it would be confusing to have it visible when a task is a cache hit
 203	            let output_with_hidden_cursor = format!("{}\x1b[?25l", output);
 204	            Self::write_output_to_parser(&pty, output_with_hidden_cursor);
 205	
 206	            self.register_pty_instance(&task_id, pty);
 207	            // Ensure the pty instances get resized appropriately
 208	            let _ = self.debounce_pty_resize();
 209	            return;
 210	        }
 211	
 212	        // If the task is continuous, ensure the pty instances get resized appropriately
 213	        if let Some(task) = self.tasks.iter_mut().find(|t| t.id == task_id) {
 214	            if task.continuous.unwrap_or(false) {
 215	                let _ = self.debounce_pty_resize();
 216	            }
 217	        }
 218	    }
 219	
 220	    pub fn end_tasks(&mut self, task_results: Vec<TaskResult>) {
 221	        // When tasks finish ensure that pty instances are resized appropriately as they may be actively displaying output when they finish
 222	        let _ = self.debounce_pty_resize();
 223	        self.dispatch_action(Action::EndTasks(task_results));
 224	    }
 225	
 226	    // Show countdown popup for the configured duration (making sure the help popup is not open first)
 227	    pub fn end_command(&mut self) {
 228	        self.console_messenger
 229	            .as_ref()
 230	            .and_then(|c| c.end_running_tasks());
 231	
 232	        // If the user has interacted with the app, or auto-exit is disabled, do nothing
 233	        if self.user_has_interacted || !self.tui_config.auto_exit.should_exit_automatically() {
 234	            return;
 235	        }
 236	
 237	        if self.tasks.len() > 1 {
 238	            self.begin_exit_countdown()
 239	        } else {
 240	            self.quit();
 241	        }
 242	    }
 243	
 244	    fn quit(&mut self) {
 245	        self.quit_at = Some(std::time::Instant::now());
 246	    }
 247	
 248	    fn begin_exit_countdown(&mut self) {
 249	        let countdown_duration = self.tui_config.auto_exit.countdown_seconds();
 250	        // If countdown is disabled, exit immediately
 251	        if countdown_duration.is_none() {
 252	            self.quit();
 253	            return;
 254	        }
 255	
 256	        // Otherwise, show the countdown popup for the configured duration
 257	        let countdown_duration = countdown_duration.unwrap() as u64;
 258	        if let Some(countdown_popup) = self
 259	            .components
 260	            .iter_mut()
 261	            .find_map(|c| c.as_any_mut().downcast_mut::<CountdownPopup>())
 262	        {
 263	            countdown_popup.start_countdown(countdown_duration);
 264	            self.update_focus(Focus::CountdownPopup);
 265	            self.quit_at = Some(
 266	                std::time::Instant::now() + std::time::Duration::from_secs(countdown_duration),
 267	            );
 268	        }
 269	    }
 270	
 271	    // A pseudo-terminal running task will provide the parser and writer directly
 272	    pub fn register_running_interactive_task(
 273	        &mut self,
 274	        task_id: String,
 275	        parser_and_writer: External<(ParserArc, WriterArc)>,
 276	    ) {
 277	        debug!("Registering interactive task: {task_id}");
 278	        let pty =
 279	            PtyInstance::interactive(parser_and_writer.0.clone(), parser_and_writer.1.clone());
 280	        self.register_running_task(task_id, pty)
 281	    }
 282	
 283	    pub fn register_running_non_interactive_task(&mut self, task_id: String) {
 284	        debug!("Registering non-interactive task: {task_id}");
 285	        let pty = PtyInstance::non_interactive();
 286	        self.register_pty_instance(&task_id, pty);
 287	        self.update_task_status(task_id.clone(), TaskStatus::InProgress);
 288	        // Ensure the pty instances get resized appropriately
 289	        let _ = self.debounce_pty_resize();
 290	    }
 291	
 292	    fn register_running_task(&mut self, task_id: String, pty: PtyInstance) {
 293	        self.register_pty_instance(&task_id, pty);
 294	        self.update_task_status(task_id.clone(), TaskStatus::InProgress);
 295	    }
 296	
 297	    pub fn append_task_output(&mut self, task_id: String, output: String) {
 298	        let pty = self
 299	            .pty_instances
 300	            .get_mut(&task_id)
 301	            .unwrap_or_else(|| panic!("{} has not been registered yet.", task_id));
 302	        pty.process_output(output.as_bytes());
 303	    }
 304	
 305	    pub fn handle_event(
 306	        &mut self,
 307	        event: tui::Event,
 308	        action_tx: &mpsc::UnboundedSender<Action>,
 309	    ) -> Result<bool> {
 310	        match event {
 311	            tui::Event::Quit => {
 312	                let _ = action_tx.send(Action::Quit);
 313	                return Ok(true);
 314	            }
 315	            tui::Event::Tick => {
 316	                let _ = action_tx.send(Action::Tick);
 317	
 318	                // Check if we have a pending resize that needs to be processed
 319	                if let Some(timer) = self.resize_debounce_timer {
 320	                    let now = std::time::SystemTime::now()
 321	                        .duration_since(std::time::UNIX_EPOCH)
 322	                        .unwrap()
 323	                        .as_millis();
 324	
 325	                    if now >= timer {
 326	                        // Timer expired, process the resize
 327	                        self.handle_pty_resize()?;
 328	                        self.resize_debounce_timer = None;
 329	                    }
 330	                }
 331	
 332	                return Ok(false);
 333	            }
 334	            tui::Event::Render => action_tx.send(Action::Render)?,
 335	            tui::Event::Resize(x, y) => action_tx.send(Action::Resize(x, y))?,
 336	            tui::Event::Key(key) => {
 337	                trace!("Handling Key Event: {:?}", key);
 338	
 339	                // If the app is in interactive mode, interactions are with
 340	                // the running task, not the app itself
 341	                if !self.is_interactive_mode() {
 342	                    // Record that the user has interacted with the app
 343	                    self.user_has_interacted = true;
 344	                }
 345	
 346	                // Handle Ctrl+C to quit, unless we're in interactive mode and the focus is on a terminal pane
 347	                if key.code == KeyCode::Char('c')
 348	                    && key.modifiers == KeyModifiers::CONTROL
 349	                    && !(matches!(self.focus, Focus::MultipleOutput(_))
 350	                        && self.is_interactive_mode())
 351	                {
 352	                    self.console_messenger
 353	                        .as_ref()
 354	                        .and_then(|c| c.end_running_tasks());
 355	
 356	                    self.is_forced_shutdown = true;
 357	                    // Quit immediately
 358	                    self.quit_at = Some(std::time::Instant::now());
 359	                    return Ok(true);
 360	                }
 361	
 362	                if matches!(self.focus, Focus::MultipleOutput(_)) && self.is_interactive_mode() {
 363	                    return match key.code {
 364	                        KeyCode::Char('z') if key.modifiers == KeyModifiers::CONTROL => {
 365	                            // Disable interactive mode when Ctrl+Z is pressed
 366	                            self.set_interactive_mode(false);
 367	                            Ok(false)
 368	                        }
 369	                        _ => {
 370	                            // The TasksList will forward the key event to the focused terminal pane
 371	                            self.handle_key_event(key).ok();
 372	                            Ok(false)
 373	                        }
 374	                    };
 375	                }
 376	
 377	                if matches!(key.code, KeyCode::F(12)) {
 378	                    self.dispatch_action(Action::ToggleDebugMode);
 379	                    return Ok(false);
 380	                }
 381	
 382	                if self.debug_mode {
 383	                    self.handle_debug_event(key);
 384	                    return Ok(false);
 385	                }
 386	
 387	                // Only handle '?' key if we're not in interactive mode and the countdown popup is not open
 388	                if matches!(key.code, KeyCode::Char('?'))
 389	                    && !self.is_interactive_mode()
 390	                    && !matches!(self.focus, Focus::CountdownPopup)
 391	                {
 392	                    let show_help_popup = !matches!(self.focus, Focus::HelpPopup);
 393	                    if let Some(help_popup) = self
 394	                        .components
 395	                        .iter_mut()
 396	                        .find_map(|c| c.as_any_mut().downcast_mut::<HelpPopup>())
 397	                    {
 398	                        help_popup.set_visible(show_help_popup);
 399	                    }
 400	                    if show_help_popup {
 401	                        self.update_focus(Focus::HelpPopup);
 402	                    } else {
 403	                        self.update_focus(self.previous_focus);
 404	                    }
 405	                    return Ok(false);
 406	                }
 407	
 408	                // If countdown popup is open, handle its keyboard events
 409	                if matches!(self.focus, Focus::CountdownPopup) {
 410	                    // Any key pressed (other than scroll keys if the popup is scrollable) will cancel the countdown
 411	                    if let Some(countdown_popup) = self
 412	                        .components
 413	                        .iter_mut()
 414	                        .find_map(|c| c.as_any_mut().downcast_mut::<CountdownPopup>())
 415	                    {
 416	                        match key.code {
 417	                            KeyCode::Char('q') => {
 418	                                // Quit immediately
 419	                                trace!("Confirming shutdown");
 420	                                self.quit_at = Some(std::time::Instant::now());
 421	                                return Ok(true);
 422	                            }
 423	                            KeyCode::Char('c') if key.modifiers == KeyModifiers::CONTROL => {
 424	                                // Quit immediately
 425	                                trace!("Confirming shutdown");
 426	                                self.quit_at = Some(std::time::Instant::now());
 427	                                return Ok(true);
 428	                            }
 429	                            KeyCode::Up | KeyCode::Char('k') if countdown_popup.is_scrollable() => {
 430	                                countdown_popup.scroll_up();
 431	                                return Ok(false);
 432	                            }
 433	                            KeyCode::Down | KeyCode::Char('j')
 434	                                if countdown_popup.is_scrollable() =>
 435	                            {
 436	                                countdown_popup.scroll_down();
 437	                                return Ok(false);
 438	                            }
 439	                            _ => {
 440	                                countdown_popup.cancel_countdown();
 441	                                self.quit_at = None;
 442	                                self.update_focus(self.previous_focus);
 443	                            }
 444	                        }
 445	                    }
 446	
 447	                    return Ok(false);
 448	                }
 449	
 450	                if let Some(tasks_list) = self
 451	                    .components
 452	                    .iter_mut()
 453	                    .find_map(|c| c.as_any_mut().downcast_mut::<TasksList>())
 454	                {
 455	                    // Handle Q to trigger countdown or immediate exit, depending on the tasks
 456	                    if !tasks_list.filter_mode && key.code == KeyCode::Char('q') {
 457	                        // Check if all tasks are in a completed state
 458	                        let all_tasks_completed = tasks_list.tasks.iter().all(|t| {
 459	                            matches!(
 460	                                t.status,
 461	                                TaskStatus::Success
 462	                                    | TaskStatus::Failure
 463	                                    | TaskStatus::Skipped
 464	                                    | TaskStatus::LocalCache
 465	                                    | TaskStatus::LocalCacheKeptExisting
 466	                                    | TaskStatus::RemoteCache
 467	                                    | TaskStatus::Stopped // Consider stopped continuous tasks as completed for exit purposes
 468	                            )
 469	                        });
 470	
 471	                        if all_tasks_completed {
 472	                            // If all tasks are done, quit immediately like Ctrl+C
 473	                            self.is_forced_shutdown = true;
 474	                            self.quit_at = Some(std::time::Instant::now());
 475	                        } else {
 476	                            // Otherwise, start the exit countdown to give the user the chance to change their mind in case of an accidental keypress
 477	                            self.is_forced_shutdown = true;
 478	                            self.begin_exit_countdown();
 479	                        }
 480	                        return Ok(false);
 481	                    }
 482	                }
 483	
 484	                // If shortcuts popup is open, handle its keyboard events
 485	                if matches!(self.focus, Focus::HelpPopup) {
 486	                    match key.code {
 487	                        KeyCode::Esc => {
 488	                            if let Some(help_popup) = self
 489	                                .components
 490	                                .iter_mut()
 491	                                .find_map(|c| c.as_any_mut().downcast_mut::<HelpPopup>())
 492	                            {
 493	                                help_popup.set_visible(false);
 494	                            }
 495	                            self.update_focus(self.previous_focus);
 496	                        }
 497	                        KeyCode::Up | KeyCode::Char('k') => {
 498	                            if let Some(help_popup) = self
 499	                                .components
 500	                                .iter_mut()
 501	                                .find_map(|c| c.as_any_mut().downcast_mut::<HelpPopup>())
 502	                            {
 503	                                help_popup.scroll_up();
 504	                            }
 505	                            return Ok(false);
 506	                        }
 507	                        KeyCode::Down | KeyCode::Char('j') => {
 508	                            if let Some(help_popup) = self
 509	                                .components
 510	                                .iter_mut()
 511	                                .find_map(|c| c.as_any_mut().downcast_mut::<HelpPopup>())
 512	                            {
 513	                                help_popup.scroll_down();
 514	                            }
 515	                            return Ok(false);
 516	                        }
 517	                        _ => {}
 518	                    }
 519	                    return Ok(false);
 520	                }
 521	
 522	                // Handle Up/Down keys for scrolling first
 523	                if matches!(self.focus, Focus::MultipleOutput(_)) {
 524	                    match key.code {
 525	                        KeyCode::Up | KeyCode::Down => {
 526	                            self.handle_key_event(key).ok();
 527	                            return Ok(false);
 528	                        }
 529	                        KeyCode::Char('k') | KeyCode::Char('j') if !self.is_interactive_mode() => {
 530	                            self.handle_key_event(key).ok();
 531	                            return Ok(false);
 532	                        }
 533	                        _ => {}
 534	                    }
 535	                }
 536	
 537	                // Get tasks list component for handling some key events
 538	                if let Some(tasks_list) = self
 539	                    .components
 540	                    .iter_mut()
 541	                    .find_map(|c| c.as_any_mut().downcast_mut::<TasksList>())
 542	                {
 543	                    match self.focus {
 544	                        Focus::MultipleOutput(_) => {
 545	                            if self.is_interactive_mode() {
 546	                                // Send all other keys to the task list (and ultimately through the terminal pane to the PTY)
 547	                                self.handle_key_event(key).ok();
 548	                            } else {
 549	                                // Handle navigation and special actions
 550	                                match key.code {
 551	                                    KeyCode::Tab => {
 552	                                        self.focus_next();
 553	                                    }
 554	                                    KeyCode::BackTab => {
 555	                                        self.focus_previous();
 556	                                    }
 557	                                    KeyCode::Esc => {
 558	                                        if !self.is_task_list_hidden() {
 559	                                            self.update_focus(Focus::TaskList);
 560	                                        }
 561	                                    }
 562	                                    // Add our new shortcuts here
 563	                                    KeyCode::Char('c') => {
 564	                                        self.handle_key_event(key).ok();
 565	                                    }
 566	                                    KeyCode::Char('u') | KeyCode::Char('d')
 567	                                        if key.modifiers.contains(KeyModifiers::CONTROL) =>
 568	                                    {
 569	                                        self.handle_key_event(key).ok();
 570	                                    }
 571	                                    KeyCode::Char('b') => {
 572	                                        self.toggle_task_list();
 573	                                    }
 574	                                    KeyCode::Char('m') => {
 575	                                        if let Some(area) = self.frame_area {
 576	                                            self.toggle_layout_mode(area);
 577	                                        }
 578	                                    }
 579	                                    _ => {
 580	                                        // Forward other keys for interactivity, scrolling (j/k) etc
 581	                                        self.handle_key_event(key).ok();
 582	                                    }
 583	                                }
 584	                            }
 585	                            return Ok(false);
 586	                        }
 587	                        _ => {
 588	                            // Handle spacebar toggle regardless of focus
 589	                            if key.code == KeyCode::Char(' ') {
 590	                                self.toggle_output_visibility();
 591	                                return Ok(false); // Skip other key handling
 592	                            }
 593	
 594	                            let is_filter_mode = tasks_list.filter_mode;
 595	
 596	                            match self.focus {
 597	                                Focus::TaskList => match key.code {
 598	                                    KeyCode::Char('j') if !is_filter_mode => {
 599	                                        self.dispatch_action(Action::NextTask);
 600	                                        let _ = self.debounce_pty_resize();
 601	                                    }
 602	                                    KeyCode::Down => {
 603	                                        self.dispatch_action(Action::NextTask);
 604	                                        let _ = self.debounce_pty_resize();
 605	                                    }
 606	                                    KeyCode::Char('k') if !is_filter_mode => {
 607	                                        self.dispatch_action(Action::PreviousTask);
 608	                                        let _ = self.debounce_pty_resize();
 609	                                    }
 610	                                    KeyCode::Up => {
 611	                                        self.dispatch_action(Action::PreviousTask);
 612	                                        let _ = self.debounce_pty_resize();
 613	                                    }
 614	                                    KeyCode::Left => {
 615	                                        self.dispatch_action(Action::PreviousPage);
 616	                                        let _ = self.debounce_pty_resize();
 617	                                    }
 618	                                    KeyCode::Right => {
 619	                                        self.dispatch_action(Action::NextPage);
 620	                                        let _ = self.debounce_pty_resize();
 621	                                    }
 622	                                    KeyCode::Esc => {
 623	                                        if matches!(self.focus, Focus::HelpPopup) {
 624	                                            if let Some(help_popup) =
 625	                                                self.components.iter_mut().find_map(|c| {
 626	                                                    c.as_any_mut().downcast_mut::<HelpPopup>()
 627	                                                })
 628	                                            {
 629	                                                help_popup.set_visible(false);
 630	                                            }
 631	                                            self.update_focus(self.previous_focus);
 632	                                        } else {
 633	                                            // Only clear filter when help popup is not in focus
 634	
 635	                                            tasks_list.clear_filter();
 636	                                        }
 637	                                    }
 638	                                    KeyCode::Char(c) => {
 639	                                        if tasks_list.filter_mode {
 640	                                            tasks_list.add_filter_char(c);
 641	                                        } else {
 642	                                            match c {
 643	                                                '/' => {
 644	                                                    if tasks_list.filter_mode {
 645	                                                        tasks_list.persist_filter();
 646	                                                    } else {
 647	                                                        tasks_list.enter_filter_mode();
 648	                                                    }
 649	                                                }
 650	                                                c => {
 651	                                                    if tasks_list.filter_mode {
 652	                                                        tasks_list.add_filter_char(c);
 653	                                                    } else {
 654	                                                        match c {
 655	                                                            'j' => {
 656	                                                                self.dispatch_action(
 657	                                                                    Action::NextTask,
 658	                                                                );
 659	                                                                let _ = self.debounce_pty_resize();
 660	                                                            }
 661	                                                            'k' => {
 662	                                                                self.dispatch_action(
 663	                                                                    Action::PreviousTask,
 664	                                                                );
 665	                                                                let _ = self.debounce_pty_resize();
 666	                                                            }
 667	                                                            '1' => {
 668	                                                                self.assign_current_task_to_pane(0);
 669	                                                                let _ = self.handle_pty_resize();
 670	                                                                // No need to debounce
 671	                                                            }
 672	                                                            '2' => {
 673	                                                                self.assign_current_task_to_pane(1);
 674	                                                                let _ = self.handle_pty_resize();
 675	                                                                // No need to debounce
 676	                                                            }
 677	                                                            '0' => self.clear_all_panes(),
 678	                                                            'h' => {
 679	                                                                self.dispatch_action(
 680	                                                                    Action::PreviousPage,
 681	                                                                );
 682	                                                                let _ = self.debounce_pty_resize();
 683	                                                            }
 684	                                                            'l' => {
 685	                                                                self.dispatch_action(
 686	                                                                    Action::NextPage,
 687	                                                                );
 688	                                                                let _ = self.debounce_pty_resize();
 689	                                                            }
 690	                                                            'b' => self.toggle_task_list(),
 691	                                                            'm' => {
 692	                                                                if let Some(area) = self.frame_area
 693	                                                                {
 694	                                                                    self.toggle_layout_mode(area);
 695	                                                                }
 696	                                                            }
 697	                                                            _ => {}
 698	                                                        }
 699	                                                    }
 700	                                                }
 701	                                            }
 702	                                        }
 703	                                    }
 704	                                    KeyCode::Backspace => {
 705	                                        if tasks_list.filter_mode {
 706	                                            tasks_list.remove_filter_char();
 707	                                        }
 708	                                    }
 709	                                    KeyCode::Tab => {
 710	                                        self.focus_next();
 711	                                    }
 712	                                    KeyCode::BackTab => {
 713	                                        self.focus_previous();
 714	                                    }
 715	                                    KeyCode::Enter if is_filter_mode => {
 716	                                        tasks_list.persist_filter();
 717	                                    }
 718	                                    KeyCode::Enter if matches!(self.focus, Focus::TaskList) => {
 719	                                        self.display_and_focus_current_task_in_terminal_pane(false);
 720	                                    }
 721	                                    _ => {}
 722	                                },
 723	                                Focus::MultipleOutput(_idx) => match key.code {
 724	                                    KeyCode::Tab => {
 725	                                        self.focus_next();
 726	                                    }
 727	                                    KeyCode::BackTab => {
 728	                                        self.focus_previous();
 729	                                    }
 730	                                    _ => {}
 731	                                },
 732	                                Focus::HelpPopup => {
 733	                                    // Shortcuts popup has its own key handling above
 734	                                }
 735	                                Focus::CountdownPopup => {
 736	                                    // Countdown popup has its own key handling above
 737	                                }
 738	                            }
 739	                        }
 740	                    }
 741	                }
 742	            }
 743	            tui::Event::Mouse(mouse) => {
 744	                // If the app is in interactive mode, interactions are with
 745	                // the running task, not the app itself
 746	                if !self.is_interactive_mode() {
 747	                    // Record that the user has interacted with the app
 748	                    self.user_has_interacted = true;
 749	                }
 750	
 751	                if matches!(self.focus, Focus::MultipleOutput(_)) {
 752	                    self.handle_mouse_event(mouse).ok();
 753	                    return Ok(false);
 754	                }
 755	
 756	                match mouse.kind {
 757	                    MouseEventKind::ScrollUp => {
 758	                        if matches!(self.focus, Focus::TaskList) {
 759	                            self.dispatch_action(Action::PreviousTask);
 760	                        } else {
 761	                            self.handle_key_event(KeyEvent::new(
 762	                                KeyCode::Up,
 763	                                KeyModifiers::empty(),
 764	                            ))
 765	                            .ok();
 766	                        }
 767	                    }
 768	                    MouseEventKind::ScrollDown => {
 769	                        if matches!(self.focus, Focus::TaskList) {
 770	                            self.dispatch_action(Action::NextTask);
 771	                        } else {
 772	                            self.handle_key_event(KeyEvent::new(
 773	                                KeyCode::Down,
 774	                                KeyModifiers::empty(),
 775	                            ))
 776	                            .ok();
 777	                        }
 778	                    }
 779	                    _ => {}
 780	                }
 781	            }
 782	            _ => {}
 783	        }
 784	
 785	        for component in self.components.iter_mut() {
 786	            if let Some(action) = component.handle_events(Some(event.clone()))? {
 787	                action_tx.send(action)?;
 788	            }
 789	        }
 790	
 791	        Ok(false)
 792	    }
