 794	    pub fn handle_action(
 795	        &mut self,
 796	        tui: &mut Tui,
 797	        action: Action,
 798	        action_tx: &UnboundedSender<Action>,
 799	    ) {
 800	        if action != Action::Tick && action != Action::Render {
 801	            trace!("{action:?}");
 802	        }
 803	        match &action {
 804	            Action::StartCommand(_) => {
 805	                self.console_messenger
 806	                    .as_ref()
 807	                    .and_then(|c| c.start_running_tasks());
 808	            }
 809	            Action::Tick => {
 810	                self.console_messenger.as_ref().and_then(|messenger| {
 811	                    self.components
 812	                        .iter()
 813	                        .find_map(|c| c.as_any().downcast_ref::<TasksList>())
 814	                        .and_then(|tasks_list| {
 815	                            messenger.update_running_tasks(&tasks_list.tasks, &self.pty_instances)
 816	                        })
 817	                });
 818	            }
 819	            // Quit immediately
 820	            Action::Quit => {
 821	                self.quit_at = Some(std::time::Instant::now());
 822	            }
 823	            // Cancel quitting
 824	            Action::CancelQuit => {
 825	                self.quit_at = None;
 826	                self.update_focus(self.previous_focus);
 827	            }
 828	            Action::Resize(w, h) => {
 829	                let rect = Rect::new(0, 0, *w, *h);
 830	                tui.resize(rect).ok();
 831	                // Update the cached frame area
 832	                self.frame_area = Some(rect);
 833	                // Recalculate the layout areas
 834	                self.recalculate_layout_areas();
 835	                // Ensure the pty instances get resized appropriately (debounced)
 836	                let _ = self.debounce_pty_resize();
 837	            }
 838	            Action::ToggleDebugMode => {
 839	                self.debug_mode = !self.debug_mode;
 840	                debug!("Debug mode: {}", self.debug_mode);
 841	            }
 842	            Action::Render => {
 843	                tui.draw(|f| {
 844	                    let area = f.area();
 845	                    // Cache the frame area if it's never been set before (will be updated in subsequent resize events if necessary)
 846	                    if self.frame_area.is_none() {
 847	                        self.frame_area = Some(area);
 848	                    }
 849	                    // Determine the required layout areas for the tasks list and terminal panes using the LayoutManager
 850	                    if self.layout_areas.is_none() {
 851	                        self.recalculate_layout_areas();
 852	                    }
 853	
 854	                    let frame_area = self.frame_area.unwrap();
 855	                    let tasks_list_hidden = self.is_task_list_hidden();
 856	                    let layout_areas = self.layout_areas.as_mut().unwrap();
 857	
 858	                    if self.debug_mode {
 859	                        let debug_widget = TuiLoggerSmartWidget::default().state(&self.debug_state);
 860	                        f.render_widget(debug_widget, frame_area);
 861	                        return;
 862	                    }
 863	
 864	                    // TODO: move this to the layout manager?
 865	                    // Check for minimum viable viewport size at the app level
 866	                    if frame_area.height < 10 || frame_area.width < 40 {
 867	                        // First ensure the frame area is at least 1x1, otherwise we can't render anything
 868	                        if frame_area.width == 0 || frame_area.height == 0 {
 869	                            return; // Can't render anything in a zero-sized area
 870	                        }
 871	
 872	                        // Create a simple message that fits in a single line to avoid scrolling issues
 873	                        let message = Line::from(vec![
 874	                            Span::styled(
 875	                                " NX ",
 876	                                Style::reset()
 877	                                    .add_modifier(Modifier::BOLD)
 878	                                    .bg(THEME.error)
 879	                                    .fg(THEME.primary_fg),
 880	                            ),
 881	                            Span::raw(" Terminal too small "),
 882	                        ]);
 883	
 884	                        // When terminal is extremely small (height < 3), render just the message
 885	                        // without any vertical padding to prevent index out of bounds errors
 886	                        if frame_area.height < 3 {
 887	                            let paragraph =
 888	                                Paragraph::new(vec![message]).alignment(Alignment::Center);
 889	
 890	                            // Create a safe area that's guaranteed to be within bounds
 891	                            let safe_area = Rect {
 892	                                x: frame_area.x,
 893	                                y: frame_area.y,
 894	                                width: frame_area
 895	                                    .width
 896	                                    .min(f.area().width.saturating_sub(frame_area.x)),
 897	                                height: frame_area
 898	                                    .height
 899	                                    .min(f.area().height.saturating_sub(frame_area.y)),
 900	                            };
 901	
 902	                            f.render_widget(paragraph, safe_area);
 903	                            return;
 904	                        }
 905	
 906	                        // For slightly larger terminals (height >= 3), use vertical padding
 907	                        let empty_line = Line::from("");
 908	                        let mut lines = vec![];
 909	
 910	                        // Add empty lines to center vertically, but cap it to prevent going out of bounds
 911	                        let vertical_padding = ((frame_area.height as usize).saturating_sub(3) / 2)
 912	                            .min(frame_area.height as usize - 1); // Ensure we leave at least 1 line for the message
 913	
 914	                        for _ in 0..vertical_padding {
 915	                            lines.push(empty_line.clone());
 916	                        }
 917	
 918	                        // Add the message
 919	                        lines.push(message);
 920	
 921	                        let paragraph = Paragraph::new(lines).alignment(Alignment::Center);
 922	
 923	                        // Create a safe area that's guaranteed to be within bounds (this can happen if the user resizes the window a lot before it stabilizes it seems)
 924	                        let safe_area = Rect {
 925	                            x: frame_area.x,
 926	                            y: frame_area.y,
 927	                            width: frame_area
 928	                                .width
 929	                                .min(f.area().width.saturating_sub(frame_area.x)),
 930	                            height: frame_area
 931	                                .height
 932	                                .min(f.area().height.saturating_sub(frame_area.y)),
 933	                        };
 934	
 935	                        f.render_widget(paragraph, safe_area);
 936	                        return;
 937	                    }
 938	
 939	                    // Draw the TaskList component, if visible
 940	                    if let Some(task_list_area) = layout_areas.task_list {
 941	                        if let Some(tasks_list) = self
 942	                            .components
 943	                            .iter_mut()
 944	                            .find_map(|c| c.as_any_mut().downcast_mut::<TasksList>())
 945	                        {
 946	                            let _ = tasks_list.draw(f, task_list_area);
 947	                        }
 948	                    }
 949	
 950	                    // Render terminal panes
 951	                    for (pane_idx, pane_area) in layout_areas.terminal_panes.iter().enumerate() {
 952	                        if let Some(tasks_list) = self
 953	                            .components
 954	                            .iter_mut()
 955	                            .find_map(|c| c.as_any_mut().downcast_mut::<TasksList>())
 956	                        {
 957	                            let relevant_pane_task: Option<String> = if self.spacebar_mode {
 958	                                // Don't unwrap - handle the None case gracefully
 959	                                self.selection_manager
 960	                                    .lock()
 961	                                    .unwrap()
 962	                                    .get_selected_task_name()
 963	                                    .cloned()
 964	                            } else {
 965	                                // Clone if it exists, but don't unwrap
 966	                                self.pane_tasks[pane_idx].clone()
 967	                            };
 968	
 969	                            if relevant_pane_task.is_none() {
 970	                                // The user has chosen to display a pane but there is currently not task assigned to it, render a placeholder
 971	                                // Render placeholder for pane 1
 972	                                let placeholder = Paragraph::new(format!(
 973	                                    "Press {} on a task to show it here",
 974	                                    pane_idx + 1
 975	                                ))
 976	                                .block(
 977	                                    Block::default()
 978	                                        .title(format!("  Output {}  ", pane_idx + 1))
 979	                                        .borders(Borders::ALL)
 980	                                        .border_style(Style::default().fg(THEME.secondary_fg)),
 981	                                )
 982	                                .style(Style::default().fg(THEME.secondary_fg))
 983	                                .alignment(Alignment::Center);
 984	
 985	                                f.render_widget(placeholder, *pane_area);
 986	                                continue;
 987	                            }
 988	
 989	                            let relevant_pane_task = relevant_pane_task.unwrap();
 990	                            if let Some(task) = tasks_list
 991	                                .tasks
 992	                                .iter_mut()
 993	                                .find(|t| t.name == relevant_pane_task)
 994	                            {
 995	                                let terminal_pane_data = &mut self.terminal_pane_data[pane_idx];
 996	                                terminal_pane_data.is_continuous = task.continuous;
 997	                                let in_progress = task.status == TaskStatus::InProgress;
 998	                                if !in_progress {
 999	                                    terminal_pane_data.set_interactive(false);
1000	                                }
1001	
1002	                                let mut has_pty = false;
1003	                                if let Some(pty) = self.pty_instances.get(&relevant_pane_task) {
1004	                                    terminal_pane_data.can_be_interactive =
1005	                                        in_progress && pty.can_be_interactive();
1006	                                    terminal_pane_data.pty = Some(pty.clone());
1007	                                    has_pty = true;
1008	                                }
1009	
1010	                                let is_focused = match self.focus {
1011	                                    Focus::MultipleOutput(focused_pane_idx) => {
1012	                                        pane_idx == focused_pane_idx
1013	                                    }
1014	                                    _ => false,
1015	                                };
1016	
1017	                                // Figure out if this pane is the next tab target
1018	                                let is_next_tab_target = !is_focused
1019	                                    && match self.focus {
1020	                                        // If the task list is focused, the next tab target is the first pane
1021	                                        Focus::TaskList => pane_idx == 0,
1022	                                        // If the first pane is focused, the next tab target is the second pane
1023	                                        Focus::MultipleOutput(0) => pane_idx == 1,
1024	                                        _ => false,
1025	                                    };
1026	
1027	                                let mut state = TerminalPaneState::new(
1028	                                    task.name.clone(),
1029	                                    task.status,
1030	                                    task.continuous,
1031	                                    is_focused,
1032	                                    has_pty,
1033	                                    is_next_tab_target,
1034	                                    self.console_messenger.is_some(),
1035	                                );
1036	
1037	                                let terminal_pane = TerminalPane::new()
1038	                                    .minimal(tasks_list_hidden && self.tasks.len() == 1)
1039	                                    .pty_data(terminal_pane_data)
1040	                                    .continuous(task.continuous);
1041	
1042	                                f.render_stateful_widget(terminal_pane, *pane_area, &mut state);
1043	                            }
1044	                        }
1045	                    }
1046	
1047	                    // Draw the help popup and countdown popup
1048	                    let (first_part, second_part) = self.components.split_at_mut(2);
1049	                    let help_popup = first_part[1]
1050	                        .as_any_mut()
1051	                        .downcast_mut::<HelpPopup>()
1052	                        .unwrap();
1053	                    let countdown_popup = second_part[0]
1054	                        .as_any_mut()
1055	                        .downcast_mut::<CountdownPopup>()
1056	                        .unwrap();
1057	                    let _ = help_popup.draw(f, frame_area);
1058	                    let _ = countdown_popup.draw(f, frame_area);
1059	                })
1060	                .ok();
1061	            }
1062	            Action::SendConsoleMessage(msg) => {
1063	                if let Some(connection) = &self.console_messenger {
1064	                    connection.send_terminal_string(msg);
1065	                } else {
1066	                    trace!("No console connection available");
1067	                }
1068	            }
1069	            _ => {}
1070	        }
1071	
1072	        // Update child components with the received action
1073	        for component in self.components.iter_mut() {
1074	            if let Ok(Some(new_action)) = component.update(action.clone()) {
1075	                action_tx.send(new_action).ok();
1076	            }
1077	        }
1078	    }
