1145	    /// Toggles the visibility of the output pane for the currently selected task.
1146	    /// In spacebar mode, the output follows the task selection.
1147	    fn toggle_output_visibility(&mut self) {
1148	        // TODO: Not sure why we do this, this action only happens when the task list is visible
1149	        self.layout_manager
1150	            .set_task_list_visibility(TaskListVisibility::Visible);
1151	
1152	        // Extract task name first to end the immutable borrow
1153	        let task_name = match self
1154	            .selection_manager
1155	            .lock()
1156	            .unwrap()
1157	            .get_selected_task_name()
1158	        {
1159	            Some(name) => name.clone(),
1160	            None => return,
1161	        };
1162	
1163	        if self.has_visible_panes() {
1164	            self.clear_all_panes();
1165	            self.set_spacebar_mode(false, None);
1166	        } else {
1167	            // Show current task in pane 1 in spacebar mode
1168	            self.pane_tasks = [Some(task_name), None];
1169	            self.set_spacebar_mode(true, None);
1170	        }
1171	    }
