1173	    fn set_spacebar_mode(
1174	        &mut self,
1175	        spacebar_mode: bool,
1176	        selection_mode_override: Option<SelectionMode>,
1177	    ) {
1178	        self.spacebar_mode = spacebar_mode;
1179	
1180	        let selection_mode = if spacebar_mode {
1181	            // When entering spacebar mode, we want to track by name by default
1182	            SelectionMode::TrackByName
1183	        } else {
1184	            // When exiting spacebar mode, we want to track by position by default
1185	            SelectionMode::TrackByPosition
1186	        };
1187	        self.selection_manager
1188	            .lock()
1189	            .unwrap()
1190	            .set_selection_mode(selection_mode_override.unwrap_or(selection_mode));
1191	
1192	        if spacebar_mode {
1193	            self.layout_manager
1194	                .set_pane_arrangement(PaneArrangement::Single);
1195	        } else {
1196	            self.layout_manager
1197	                .set_pane_arrangement(PaneArrangement::None);
1198	        }
1199	
1200	        // Recalculate the layout areas
1201	        self.recalculate_layout_areas();
1202	
1203	        // Ensure the pty instances get resized appropriately
1204	        let _ = self.debounce_pty_resize();
1205	
1206	        self.dispatch_action(Action::SetSpacebarMode(spacebar_mode));
1207	    }
1208	
1209	    fn focus_next(&mut self) {
1210	        if !self.has_visible_panes() {
1211	            return;
1212	        }
1213	
1214	        let focus = match self.focus {
1215	            Focus::TaskList => {
1216	                // Move to first visible pane
1217	                if let Some(first_pane) = self.pane_tasks.iter().position(|t| t.is_some()) {
1218	                    Focus::MultipleOutput(first_pane)
1219	                } else {
1220	                    Focus::TaskList
1221	                }
1222	            }
1223	            Focus::MultipleOutput(current_pane) => {
1224	                // Find next visible pane or go back to task list
1225	                let next_pane = (current_pane + 1..2).find(|&idx| self.pane_tasks[idx].is_some());
1226	
1227	                match next_pane {
1228	                    Some(pane) => Focus::MultipleOutput(pane),
1229	                    None => {
1230	                        // If the task list is hidden, try and go back to the previous pane if there is one, otherwise do nothing
1231	                        if self.is_task_list_hidden() {
1232	                            if current_pane > 0 {
1233	                                Focus::MultipleOutput(current_pane - 1)
1234	                            } else {
1235	                                return;
1236	                            }
1237	                        } else {
1238	                            Focus::TaskList
1239	                        }
1240	                    }
1241	                }
1242	            }
1243	            Focus::HelpPopup => Focus::TaskList,
1244	            Focus::CountdownPopup => Focus::TaskList,
1245	        };
1246	
1247	        self.update_focus(focus);
1248	    }
1249	
1250	    fn focus_previous(&mut self) {
1251	        let num_panes = self.pane_tasks.iter().filter(|t| t.is_some()).count();
1252	        if num_panes == 0 {
1253	            return; // No panes to focus
1254	        }
1255	
1256	        let focus = match self.focus {
1257	            Focus::TaskList => {
1258	                // When on task list, go to the rightmost (highest index) pane
1259	                if let Some(last_pane) = (0..2).rev().find(|&idx| self.pane_tasks[idx].is_some()) {
1260	                    Focus::MultipleOutput(last_pane)
1261	                } else {
1262	                    Focus::TaskList
1263	                }
1264	            }
1265	            Focus::MultipleOutput(current_pane) => {
1266	                if current_pane > 0 {
1267	                    // Try to go to previous pane
1268	                    if let Some(prev_pane) = (0..current_pane)
1269	                        .rev()
1270	                        .find(|&idx| self.pane_tasks[idx].is_some())
1271	                    {
1272	                        Focus::MultipleOutput(prev_pane)
1273	                    } else if !self.is_task_list_hidden() {
1274	                        // Go to task list if it's visible
1275	                        Focus::TaskList
1276	                    } else {
1277	                        // If task list is hidden, wrap around to rightmost pane
1278	                        if let Some(last_pane) =
1279	                            (0..2).rev().find(|&idx| self.pane_tasks[idx].is_some())
1280	                        {
1281	                            Focus::MultipleOutput(last_pane)
1282	                        } else {
1283	                            // Shouldn't happen (would mean no panes)
1284	                            return;
1285	                        }
1286	                    }
1287	                } else {
1288	                    // We're at leftmost pane (index 0)
1289	                    if !self.is_task_list_hidden() {
1290	                        // Go to task list if it's visible
1291	                        Focus::TaskList
1292	                    } else if num_panes > 1 {
1293	                        // If task list hidden and multiple panes, wrap to rightmost pane
1294	                        if let Some(last_pane) =
1295	                            (1..2).rev().find(|&idx| self.pane_tasks[idx].is_some())
1296	                        {
1297	                            Focus::MultipleOutput(last_pane)
1298	                        } else {
1299	                            // Stay on current pane if can't find another one
1300	                            Focus::MultipleOutput(current_pane)
1301	                        }
1302	                    } else {
1303	                        // Only one pane and task list hidden, nowhere to go
1304	                        Focus::MultipleOutput(current_pane)
1305	                    }
1306	                }
1307	            }
1308	            Focus::HelpPopup => Focus::TaskList,
1309	            Focus::CountdownPopup => Focus::TaskList,
1310	        };
1311	
1312	        self.update_focus(focus);
1313	    }
