1333	    fn assign_current_task_to_pane(&mut self, pane_idx: usize) {
1334	        // Extract task name first to end the immutable borrow
1335	        let task_name = match self
1336	            .selection_manager
1337	            .lock()
1338	            .unwrap()
1339	            .get_selected_task_name()
1340	        {
1341	            Some(name) => name.clone(),
1342	            None => return,
1343	        };
1344	
1345	        // If we're in spacebar mode and this is pane 0, convert to pinned mode
1346	        if self.spacebar_mode && pane_idx == 0 {
1347	            // When converting from spacebar to pinned, stay in name-tracking mode
1348	            self.set_spacebar_mode(false, Some(SelectionMode::TrackByName));
1349	            if self.layout_manager.get_pane_arrangement() == PaneArrangement::None {
1350	                self.layout_manager
1351	                    .set_pane_arrangement(PaneArrangement::Single);
1352	            }
1353	            self.dispatch_action(Action::PinTask(task_name.clone(), pane_idx));
1354	        } else {
1355	            // Check if the task is already pinned to the pane
1356	            if self.pane_tasks[pane_idx].as_deref() == Some(task_name.as_str()) {
1357	                // Unpin the task if it's already pinned
1358	                self.pane_tasks[pane_idx] = None;
1359	
1360	                // If this was previously pane 2 and its now unpinned and pane 1 is still set, set the pane arrangement to single
1361	                if pane_idx == 1 && self.pane_tasks[0].is_some() {
1362	                    self.layout_manager
1363	                        .set_pane_arrangement(PaneArrangement::Single);
1364	                }
1365	
1366	                // Adjust focused pane if necessary
1367	                if !self.has_visible_panes() {
1368	                    self.update_focus(Focus::TaskList);
1369	                    // When all panes are cleared, use position-based selection
1370	                    self.set_spacebar_mode(false, Some(SelectionMode::TrackByPosition));
1371	                    // If no visible panes are left, set the pane arrangement to none
1372	                    self.layout_manager
1373	                        .set_pane_arrangement(PaneArrangement::None);
1374	                }
1375	
1376	                self.dispatch_action(Action::UnpinTask(task_name.clone(), pane_idx));
1377	            } else {
1378	                // Pin the task to the specified pane
1379	                self.pane_tasks[pane_idx] = Some(task_name.clone());
1380	                self.update_focus(Focus::TaskList);
1381	
1382	                // Exit spacebar mode when pinning
1383	                // When pinning a task, use name-based selection
1384	                self.set_spacebar_mode(false, Some(SelectionMode::TrackByName));
1385	
1386	                if self.layout_manager.get_pane_arrangement() == PaneArrangement::None {
1387	                    if pane_idx == 0 && self.pane_tasks[1].is_none() {
1388	                        self.layout_manager
1389	                            .set_pane_arrangement(PaneArrangement::Single);
1390	                    } else if pane_idx == 1 || pane_idx == 0 && self.pane_tasks[1].is_some() {
1391	                        self.layout_manager
1392	                            .set_pane_arrangement(PaneArrangement::Double);
1393	                    }
1394	                }
1395	
1396	                self.dispatch_action(Action::PinTask(task_name.clone(), pane_idx));
1397	            }
1398	        }
1399	
1400	        // Always re-evaluate the optimal size of the terminal pane(s)
1401	        self.recalculate_layout_areas();
1402	        // Ensure the pty instances get resized appropriately (no debounce as this is based on an imperative user action)
1403	        let _ = self.handle_pty_resize();
