1442	    /// Ensures that the PTY instances get resized appropriately based on the latest layout areas.
1443	    fn debounce_pty_resize(&mut self) -> io::Result<()> {
1444	        // Get current time in milliseconds
1445	        let now = std::time::SystemTime::now()
1446	            .duration_since(std::time::UNIX_EPOCH)
1447	            .unwrap()
1448	            .as_millis();
1449	
1450	        // If we have a timer and it's not expired yet, just return
1451	        if let Some(timer) = self.resize_debounce_timer {
1452	            if now < timer {
1453	                return Ok(());
1454	            }
1455	        }
1456	
1457	        // Set a new timer for 200ms from now
1458	        self.resize_debounce_timer = Some(now + 200);
1459	
1460	        // Process the resize
1461	        self.handle_pty_resize()
1462	    }
