1464	    /// Actually processes the resize event by updating PTY dimensions.
1465	    fn handle_pty_resize(&mut self) -> io::Result<()> {
1466	        if self.layout_areas.is_none() {
1467	            return Ok(());
1468	        }
1469	
1470	        let mut needs_sort = false;
1471	
1472	        for (pane_idx, pane_area) in self
1473	            .layout_areas
1474	            .as_ref()
1475	            .unwrap()
1476	            .terminal_panes
1477	            .iter()
1478	            .enumerate()
1479	        {
1480	            let terminal_pane_data = &mut self.terminal_pane_data[pane_idx];
1481	            if let Some(pty) = terminal_pane_data.pty.as_ref() {
1482	                let (pty_height, pty_width) = TerminalPane::calculate_pty_dimensions(*pane_area);
1483	
1484	                // Get current dimensions before resize
1485	                let old_rows = if let Some(screen) = pty.get_screen() {
1486	                    let (rows, _) = screen.size();
1487	                    rows
1488	                } else {
1489	                    0
1490	                };
1491	                let mut pty_clone = pty.as_ref().clone();
1492	                pty_clone.resize(pty_height, pty_width)?;
1493	
1494	                // If dimensions changed, mark for sort
1495	                if old_rows != pty_height {
1496	                    needs_sort = true;
1497	                }
1498	            }
1499	        }
1500	
1501	        // Sort tasks if needed after all resizing is complete
1502	        if needs_sort {
1503	            self.dispatch_action(Action::SortTasks);
1504	        }
1505	
1506	        Ok(())
1507	    }
