1544	    fn handle_debug_event(&mut self, key: KeyEvent) {
1545	        // https://docs.rs/tui-logger/latest/tui_logger/#smart-widget-key-commands
1546	        // |  KEY     | ACTION
1547	        // |----------|-----------------------------------------------------------|
1548	        // | h        | Toggles target selector widget hidden/visible
1549	        // | f        | Toggle focus on the selected target only
1550	        // | UP       | Select previous target in target selector widget
1551	        // | DOWN     | Select next target in target selector widget
1552	        // | LEFT     | Reduce SHOWN (!) log messages by one level
1553	        // | RIGHT    | Increase SHOWN (!) log messages by one level
1554	        // | -        | Reduce CAPTURED (!) log messages by one level
1555	        // | +        | Increase CAPTURED (!) log messages by one level
1556	        // | PAGEUP   | Enter Page Mode and scroll approx. half page up in log history.
1557	        // | PAGEDOWN | Only in page mode: scroll 10 events down in log history.
1558	        // | ESCAPE   | Exit page mode and go back to scrolling mode
1559	        // | SPACE    | Toggles hiding of targets, which have logfilter set to off
1560	        let debug_widget_event = match key.code {
1561	            KeyCode::Char(' ') => Some(TuiWidgetEvent::SpaceKey),
1562	            KeyCode::Esc => Some(TuiWidgetEvent::EscapeKey),
1563	            KeyCode::PageUp => Some(TuiWidgetEvent::PrevPageKey),
1564	            KeyCode::PageDown => Some(TuiWidgetEvent::NextPageKey),
1565	            KeyCode::Up => Some(TuiWidgetEvent::UpKey),
1566	            KeyCode::Down => Some(TuiWidgetEvent::DownKey),
1567	            KeyCode::Left => Some(TuiWidgetEvent::LeftKey),
1568	            KeyCode::Right => Some(TuiWidgetEvent::RightKey),
1569	            KeyCode::Char('+') => Some(TuiWidgetEvent::PlusKey),
1570	            KeyCode::Char('-') => Some(TuiWidgetEvent::MinusKey),
1571	            KeyCode::Char('h') => Some(TuiWidgetEvent::HideKey),
1572	            KeyCode::Char('f') => Some(TuiWidgetEvent::FocusKey),
1573	            _ => None,
1574	        };
1575	
1576	        if let Some(event) = debug_widget_event {
1577	            self.debug_state.transition(event);
1578	        }
1579	    }
