pub fn update_status(&mut self, status: TaskStatus) {
    self.status = status;
    // Update the cache_status label that gets printed in the UI
    if self.continuous {
        self.cache_status = CACHE_STATUS_NOT_APPLICABLE.to_string();
    } else {
        self.cache_status = match status {
            TaskStatus::InProgress => CACHE_STATUS_NOT_YET_KNOWN.to_string(),
            TaskStatus::LocalCacheKeptExisting => CACHE_STATUS_LOCAL_KEPT_EXISTING.to_string(),
            TaskStatus::LocalCache => CACHE_STATUS_LOCAL.to_string(),
            TaskStatus::RemoteCache => CACHE_STATUS_REMOTE.to_string(),
            _ => CACHE_STATUS_NOT_APPLICABLE.to_string(),
        }
    }
}

/// Updates the duration display for in-progress tasks
pub fn update_duration(&mut self) {
    if self.continuous {
        self.duration = "Continuous".to_string();
    } else if self.status == TaskStatus::InProgress && self.start_time.is_some() {
        let current_time = current_time_ms();
        self.duration = format_duration_since(self.start_time.unwrap(), current_time);
    } else if self.start_time.is_some() && self.end_time.is_some() {
        // Task completed - use final duration
        self.duration = format_duration_since(self.start_time.unwrap(), self.end_time.unwrap());
    } else {
        self.duration = DURATION_NOT_YET_KNOWN.to_string();
    }
}

