 30‚Üí
 31‚Üí/**
 32‚Üí * Refactored Maven plugin that analyzes Maven projects for Nx integration.
 33‚Üí * This version delegates complex logic to specialized service classes.
 34‚Üí */
 35‚Üí@Mojo(name = "analyze", aggregator = true, requiresDependencyResolution = ResolutionScope.NONE)
 36‚Üípublic class NxAnalyzerMojo extends AbstractMojo {
 37‚Üí    
 38‚Üí    @Parameter(defaultValue = "${session}", readonly = true, required = true)
 39‚Üí    private MavenSession session;
 40‚Üí    
 41‚Üí    @Parameter(defaultValue = "${reactorProjects}", readonly = true, required = true)
 42‚Üí    private List<MavenProject> reactorProjects;
 43‚Üí    
 44‚Üí    @Parameter(property = "nx.outputFile")
 45‚Üí    private String outputFile;
 46‚Üí    
 47‚Üí    @Parameter(property = "nx.verbose", defaultValue = "false")
 48‚Üí    private String verboseStr;
 49‚Üí    
 50‚Üí    @Component
 51‚Üí    private LifecycleExecutor lifecycleExecutor;
 52‚Üí    
 53‚Üí    @Component
 54‚Üí    private DefaultLifecycles defaultLifecycles;
 55‚Üí    
 56‚Üí    // Services for delegating complex operations
 57‚Üí    private ExecutionPlanAnalysisService executionPlanAnalysisService;
 58‚Üí    private TargetGenerationService targetGenerationService;
 59‚Üí    private TargetGroupService targetGroupService;
 60‚Üí    private TargetDependencyService targetDependencyService;
 61‚Üí    
 62‚Üí    @Override
 63‚Üí    public void execute() throws MojoExecutionException, MojoFailureException {
 64‚Üí        long overallStartTime = System.currentTimeMillis();
 65‚Üí        
 66‚Üí        getLog().info("üî• Starting Maven analysis for " + reactorProjects.size() + " projects...");
 67‚Üí        
 68‚Üí        try {
 69‚Üí            // Phase 1: Initialize services
 70‚Üí            long initStart = System.currentTimeMillis();
 71‚Üí            initializeServices();
 72‚Üí            long initDuration = System.currentTimeMillis() - initStart;
 73‚Üí            getLog().info("‚è±Ô∏è  Service initialization completed in " + initDuration + "ms");
 74‚Üí            
 75‚Üí            logBasicInfo();
 76‚Üí            
 77‚Üí            // Phase 2: Perform Maven analysis
 78‚Üí            long analysisStart = System.currentTimeMillis();
 79‚Üí            getLog().info("üìä Starting Maven workspace analysis...");
 80‚Üí            Map<String, Object> result = performAnalysis();
 81‚Üí            long analysisDuration = System.currentTimeMillis() - analysisStart;
 82‚Üí            getLog().info("‚è±Ô∏è  Maven workspace analysis completed in " + analysisDuration + "ms (" + 
 83‚Üí                         String.format("%.2f", analysisDuration / 1000.0) + "s)");
 84‚Üí            
 85‚Üí            // Phase 3: Write output
 86‚Üí            long writeStart = System.currentTimeMillis();
 87‚Üí            String outputPath = determineOutputPath();
 88‚Üí            writeResult(result, outputPath);
 89‚Üí            long writeDuration = System.currentTimeMillis() - writeStart;
 90‚Üí            getLog().info("‚è±Ô∏è  Output file writing completed in " + writeDuration + "ms");
 91‚Üí            
 92‚Üí            logCompletion(overallStartTime, outputPath, analysisDuration);
 93‚Üí            
 94‚Üí        } catch (Exception e) {
 95‚Üí            long failureDuration = System.currentTimeMillis() - overallStartTime;
 96‚Üí            getLog().error("‚ùå Analysis failed after " + failureDuration + "ms: " + e.getMessage());
 97‚Üí            throw new MojoExecutionException("Analysis failed: " + e.getMessage(), e);
 98‚Üí        }
 99‚Üí    }
100‚Üí    
101‚Üí    private void initializeServices() {
102‚Üí        this.executionPlanAnalysisService = new ExecutionPlanAnalysisService(getLog(), isVerbose(), lifecycleExecutor, session, defaultLifecycles);
103‚Üí        this.targetGenerationService = new TargetGenerationService(getLog(), isVerbose(), session, executionPlanAnalysisService);
104‚Üí        this.targetGroupService = new TargetGroupService(executionPlanAnalysisService);
105‚Üí        this.targetDependencyService = new TargetDependencyService(getLog(), isVerbose(), executionPlanAnalysisService);
106‚Üí    }
107‚Üí    
108‚Üí    private void logBasicInfo() {
109‚Üí        if (isVerbose()) {
110‚Üí            getLog().info("Verbose mode enabled");
111‚Üí        }
112‚Üí        
113‚Üí        getLog().info("Root directory: " + session.getExecutionRootDirectory());
114‚Üí        if (!reactorProjects.isEmpty()) {
115‚Üí            MavenProject first = reactorProjects.get(0);
116‚Üí            MavenProject last = reactorProjects.get(reactorProjects.size() - 1);
117‚Üí            getLog().info("First project: " + first.getGroupId() + ":" + first.getArtifactId());
118‚Üí            getLog().info("Last project: " + last.getGroupId() + ":" + last.getArtifactId());
119‚Üí        }
120‚Üí    }
121‚Üí    
122‚Üí    private Map<String, Object> performAnalysis() {
123‚Üí        File workspaceRoot = new File(session.getExecutionRootDirectory());
124‚Üí        
125‚Üí        // Phase 1: Calculate project dependencies
126‚Üí        long depCalcStart = System.currentTimeMillis();
127‚Üí        Map<MavenProject, List<MavenProject>> projectDependencies = calculateProjectDependencies();
128‚Üí        long depCalcDuration = System.currentTimeMillis() - depCalcStart;
129‚Üí        getLog().info("‚è±Ô∏è  Project dependency calculation completed in " + depCalcDuration + "ms");
130‚Üí        
131‚Üí        // Phase 2: Generate targets and groups for each project
132‚Üí        long targetGenStart = System.currentTimeMillis();
133‚Üí        Map<MavenProject, Map<String, TargetConfiguration>> projectTargets = new LinkedHashMap<>();
134‚Üí        Map<MavenProject, Map<String, TargetGroup>> projectTargetGroups = new LinkedHashMap<>();
135‚Üí        
136‚Üí        int processedProjects = 0;
137‚Üí        for (MavenProject project : reactorProjects) {
138‚Üí            long projectStart = System.currentTimeMillis();
139‚Üí            try {
140‚Üí                // Get actual project dependencies for this project (not all reactor projects)
141‚Üí                List<MavenProject> actualDependencies = projectDependencies.getOrDefault(project, new ArrayList<>());
142‚Üí                
143‚Üí                // Calculate goal dependencies using only actual dependencies
144‚Üí                Map<String, List<String>> goalDependencies = calculateGoalDependencies(project, actualDependencies);
145‚Üí                
146‚Üí                // Generate targets using pre-calculated goal dependencies (phase dependencies calculated later)
147‚Üí                Map<String, TargetConfiguration> targets = targetGenerationService.generateTargets(
148‚Üí                    project, workspaceRoot, goalDependencies, new LinkedHashMap<>());
149‚Üí                
150‚Üí                // Now calculate phase dependencies using the generated targets
151‚Üí                Map<String, List<String>> phaseDependencies = calculatePhaseDependencies(project, targets);
152‚Üí                
153‚Üí                // Update phase targets with calculated dependencies
154‚Üí                updatePhaseTargetsWithDependencies(targets, phaseDependencies);
155‚Üí                projectTargets.put(project, targets);
156‚Üí                
157‚Üí                // Generate target groups
158‚Üí                Map<String, TargetGroup> targetGroups = targetGroupService.generateTargetGroups(project, targets, session);
159‚Üí                projectTargetGroups.put(project, targetGroups);
160‚Üí                
161‚Üí                processedProjects++;
162‚Üí                long projectDuration = System.currentTimeMillis() - projectStart;
163‚Üí                
164‚Üí                if (isVerbose()) {
165‚Üí                    getLog().info("‚úÖ Processed " + project.getArtifactId() + " in " + projectDuration + "ms" +
166‚Üí                                 ": " + targets.size() + " targets, " + targetGroups.size() + " groups, " +
167‚Üí                                 actualDependencies.size() + " project dependencies");
168‚Üí                } else if (processedProjects % 100 == 0) {
169‚Üí                    // Log progress every 100 projects for large workspaces
170‚Üí                    long avgTimePerProject = (System.currentTimeMillis() - targetGenStart) / processedProjects;
171‚Üí                    getLog().info("üìä Processed " + processedProjects + "/" + reactorProjects.size() + 
172‚Üí                                 " projects (avg " + avgTimePerProject + "ms per project)");
173‚Üí                }
174‚Üí                
175‚Üí            } catch (Exception e) {
176‚Üí                getLog().error("Error processing project " + project.getArtifactId() + ": " + e.getMessage(), e);
177‚Üí                // Continue with empty targets and groups
178‚Üí                projectTargets.put(project, new LinkedHashMap<>());
179‚Üí                projectTargetGroups.put(project, new LinkedHashMap<>());
180‚Üí            }
181‚Üí        }
182‚Üí        
183‚Üí        long targetGenDuration = System.currentTimeMillis() - targetGenStart;
184‚Üí        getLog().info("‚è±Ô∏è  Target generation completed in " + targetGenDuration + "ms for " + 
185‚Üí                     processedProjects + " projects (avg " + (targetGenDuration / processedProjects) + "ms per project)");
186‚Üí        
187‚Üí        // Phase 3: Generate Nx-compatible outputs
188‚Üí        long outputGenStart = System.currentTimeMillis();
189‚Üí        List<CreateNodesV2Entry> createNodesEntries = CreateNodesResultGenerator.generateCreateNodesV2Results(
190‚Üí            reactorProjects, workspaceRoot, projectTargets, projectTargetGroups);
191‚Üí        
192‚Üí        List<Object[]> createNodesResults = new ArrayList<>();
193‚Üí        for (CreateNodesV2Entry entry : createNodesEntries) {
194‚Üí            createNodesResults.add(entry.toArray());
195‚Üí        }
196‚Üí        
197‚Üí        List<RawProjectGraphDependency> createDependencies = CreateDependenciesGenerator.generateCreateDependencies(
198‚Üí            reactorProjects, workspaceRoot, getLog(), isVerbose());
199‚Üí        
200‚Üí        long outputGenDuration = System.currentTimeMillis() - outputGenStart;
201‚Üí        getLog().info("‚è±Ô∏è  Nx output generation completed in " + outputGenDuration + "ms");
202‚Üí        
203‚Üí        if (isVerbose()) {
204‚Üí            getLog().info("Generated " + createDependencies.size() + " workspace dependencies");
205‚Üí        }
206‚Üí        
207‚Üí        Map<String, Object> result = new LinkedHashMap<>();
208‚Üí        result.put("createNodesResults", createNodesResults);
209‚Üí        result.put("createDependencies", createDependencies);
210‚Üí        
211‚Üí        return result;
212‚Üí    }
213‚Üí    
214‚Üí    /**
215‚Üí     * Calculate actual project dependencies for all projects in the reactor.
216‚Üí     * Returns a map from each project to its list of actual Maven dependency projects.
217‚Üí     */
218‚Üí    private Map<MavenProject, List<MavenProject>> calculateProjectDependencies() {
219‚Üí        Map<MavenProject, List<MavenProject>> projectDependencies = new LinkedHashMap<>();
220‚Üí        
221‚Üí        // Build artifact mapping for workspace projects
222‚Üí        Map<String, MavenProject> artifactToProject = new HashMap<>();
223‚Üí        for (MavenProject project : reactorProjects) {
224‚Üí            if (project.getGroupId() != null && project.getArtifactId() != null) {
225‚Üí                String key = MavenUtils.formatProjectKey(project);
226‚Üí                artifactToProject.put(key, project);
227‚Üí            }
228‚Üí        }
229‚Üí        
230‚Üí        // Calculate dependencies for each project
231‚Üí        for (MavenProject project : reactorProjects) {
232‚Üí            List<MavenProject> dependencies = new ArrayList<>();
233‚Üí            
234‚Üí            if (project.getDependencies() != null) {
235‚Üí                for (org.apache.maven.model.Dependency dep : project.getDependencies()) {
236‚Üí                    if (dep.getGroupId() != null && dep.getArtifactId() != null) {
237‚Üí                        String depKey = dep.getGroupId() + ":" + dep.getArtifactId();
238‚Üí                        
239‚Üí                        // Check if this dependency refers to another project in workspace
240‚Üí                        MavenProject targetProject = artifactToProject.get(depKey);
241‚Üí                        if (targetProject != null && !targetProject.equals(project)) {
242‚Üí                            dependencies.add(targetProject);
243‚Üí                        }
244‚Üí                    }
245‚Üí                }
246‚Üí            }
247‚Üí            
248‚Üí            projectDependencies.put(project, dependencies);
249‚Üí            
250‚Üí            if (isVerbose() && !dependencies.isEmpty()) {
251‚Üí                getLog().debug("Project " + project.getArtifactId() + " depends on " + 
252‚Üí                             dependencies.size() + " workspace projects");
253‚Üí            }
254‚Üí        }
255‚Üí        
256‚Üí        return projectDependencies;
257‚Üí    }
258‚Üí
259‚Üí    /**
260‚Üí     * Calculate goal dependencies for a project using only actual project dependencies
261‚Üí     */
262‚Üí    private Map<String, List<String>> calculateGoalDependencies(MavenProject project, List<MavenProject> actualDependencies) {
263‚Üí        Map<String, List<String>> goalDependencies = new LinkedHashMap<>();
264‚Üí        
265‚Üí        if (isVerbose()) {
266‚Üí            getLog().debug("Calculating goal dependencies for " + project.getArtifactId());
267‚Üí        }
268‚Üí        
269‚Üí        // First pass: collect all potential goal targets
270‚Üí        Set<String> goalTargets = collectGoalTargets(project);
271‚Üí        
272‚Üí        // Calculate dependencies for each goal target
273‚Üí        for (String targetName : goalTargets) {
274‚Üí            String goal = ExecutionPlanAnalysisService.extractGoalFromTargetName(targetName);
275‚Üí            
276‚Üí            // Try to find execution phase from plugin configuration
277‚Üí            String executionPhase = findExecutionPhase(project, targetName);
278‚Üí            
279‚Üí            List<String> dependencies = targetDependencyService.calculateGoalDependencies(
280‚Üí                project, executionPhase, targetName, actualDependencies);
281‚Üí            goalDependencies.put(targetName, dependencies);
282‚Üí        }
283‚Üí        
284‚Üí        return goalDependencies;
285‚Üí    }
286‚Üí    
287‚Üí    /**
288‚Üí     * Calculate phase dependencies for a project using generated targets
289‚Üí     */
290‚Üí    private Map<String, List<String>> calculatePhaseDependencies(MavenProject project, Map<String, TargetConfiguration> allTargets) {
291‚Üí        Map<String, List<String>> phaseDependencies = new LinkedHashMap<>();
292‚Üí        
293‚Üí        // Get all lifecycle phases from all Maven lifecycles (default, clean, site)
294‚Üí        Set<String> allPhases = executionPlanAnalysisService.getAllLifecyclePhases();
295‚Üí        
296‚Üí        if (isVerbose()) {
297‚Üí            getLog().debug("Calculating dependencies for " + allPhases.size() + " lifecycle phases: " + allPhases);
298‚Üí        }
299‚Üí        
300‚Üí        for (String phase : allPhases) {
301‚Üí            List<String> dependencies = targetDependencyService.calculatePhaseDependencies(
302‚Üí                phase, allTargets, project, reactorProjects);
303‚Üí            phaseDependencies.put(phase, dependencies);
304‚Üí        }
305‚Üí        
306‚Üí        return phaseDependencies;
307‚Üí    }
308‚Üí    
309‚Üí    /**
310‚Üí     * Update phase targets with calculated dependencies
311‚Üí     */
312‚Üí    private void updatePhaseTargetsWithDependencies(Map<String, TargetConfiguration> targets, 
313‚Üí                                                   Map<String, List<String>> phaseDependencies) {
314‚Üí        for (Map.Entry<String, List<String>> entry : phaseDependencies.entrySet()) {
315‚Üí            String phase = entry.getKey();
316‚Üí            List<String> dependencies = entry.getValue();
317‚Üí            
318‚Üí            TargetConfiguration phaseTarget = targets.get(phase);
319‚Üí            if (phaseTarget != null) {
320‚Üí                phaseTarget.setDependsOn(dependencies);
321‚Üí            }
322‚Üí        }
323‚Üí    }
324‚Üí    
325‚Üí    /**
326‚Üí     * Collect all potential goal targets from project plugins
327‚Üí     */
328‚Üí    private Set<String> collectGoalTargets(MavenProject project) {
329‚Üí        Set<String> goalTargets = new LinkedHashSet<>();
330‚Üí        
331‚Üí        if (isVerbose()) {
332‚Üí            getLog().debug("Collecting goals for " + project.getArtifactId() + " (" + 
333‚Üí                          (project.getBuildPlugins() != null ? project.getBuildPlugins().size() : 0) + " plugins)");
334‚Üí        }
335‚Üí        
336‚Üí        if (project.getBuildPlugins() != null) {
337‚Üí            project.getBuildPlugins().forEach(plugin -> {
338‚Üí                String artifactId = plugin.getArtifactId();
339‚Üí                
340‚Üí                // Add goals from executions
341‚Üí                if (plugin.getExecutions() != null) {
342‚Üí                    plugin.getExecutions().forEach(execution -> {
343‚Üí                        if (execution.getGoals() != null) {
344‚Üí                            execution.getGoals().forEach(goal -> {
345‚Üí                                String targetName = ExecutionPlanAnalysisService.getTargetName(artifactId, goal);
346‚Üí                                goalTargets.add(targetName);
347‚Üí                            });
348‚Üí                        }
349‚Üí                    });
350‚Üí                }
351‚Üí                
352‚Üí                // Add common goals for well-known plugins
353‚Üí                addCommonGoals(artifactId, goalTargets);
354‚Üí            });
355‚Üí        }
356‚Üí        
357‚Üí        if (isVerbose()) {
358‚Üí            getLog().debug("Found " + goalTargets.size() + " goals for " + project.getArtifactId());
359‚Üí        }
360‚Üí        return goalTargets;
361‚Üí    }
362‚Üí    
363‚Üí    /**
364‚Üí     * Find execution phase for a goal target
365‚Üí     */
366‚Üí    private String findExecutionPhase(MavenProject project, String targetName) {
367‚Üí        String goal = ExecutionPlanAnalysisService.extractGoalFromTargetName(targetName);
368‚Üí        
369‚Üí        if (project.getBuildPlugins() != null) {
370‚Üí            for (org.apache.maven.model.Plugin plugin : project.getBuildPlugins()) {
371‚Üí                if (plugin.getExecutions() != null) {
372‚Üí                    for (org.apache.maven.model.PluginExecution execution : plugin.getExecutions()) {
373‚Üí                        if (execution.getGoals() != null && execution.getGoals().contains(goal)) {
374‚Üí                            return execution.getPhase();
375‚Üí                        }
376‚Üí                    }
377‚Üí                }
378‚Üí            }
379‚Üí        }
380‚Üí        
381‚Üí        return null; // Will trigger phase inference in dependency service
382‚Üí    }
383‚Üí    
384‚Üí    /**
385‚Üí     * Add common goals for well-known plugins
386‚Üí     */
387‚Üí    private void addCommonGoals(String artifactId, Set<String> goalTargets) {
388‚Üí        List<String> commonGoals = ExecutionPlanAnalysisService.getCommonGoalsForPlugin(artifactId);
389‚Üí        for (String goal : commonGoals) {
390‚Üí            goalTargets.add(ExecutionPlanAnalysisService.getTargetName(artifactId, goal));
391‚Üí        }
392‚Üí    }
393‚Üí    
394‚Üí    
395‚Üí    private boolean isVerbose() {
396‚Üí        String systemProp = System.getProperty("nx.verbose");
397‚Üí        boolean fromParam = "true".equalsIgnoreCase(verboseStr);
398‚Üí        boolean fromSystem = "true".equalsIgnoreCase(systemProp);
399‚Üí        return fromParam || fromSystem;
400‚Üí    }
401‚Üí    
402‚Üí    private String determineOutputPath() {
403‚Üí        if (outputFile != null && !outputFile.isEmpty()) {
404‚Üí            return outputFile;
405‚Üí        }
406‚Üí        return new File(session.getExecutionRootDirectory(), "maven-analysis.json").getAbsolutePath();
407‚Üí    }
408‚Üí    
409‚Üí    private void writeResult(Map<String, Object> result, String outputPath) throws IOException {
410‚Üí        Gson gson = new GsonBuilder()
411‚Üí            .setPrettyPrinting()
412‚Üí            .serializeNulls()
413‚Üí            .create();
414‚Üí            
415‚Üí        try (FileWriter writer = new FileWriter(outputPath)) {
416‚Üí            gson.toJson(result, writer);
417‚Üí        }
418‚Üí        
419‚Üí        if (isVerbose()) {
420‚Üí            long fileSize = new File(outputPath).length();
421‚Üí            getLog().info("Wrote " + (fileSize / 1024) + "KB analysis results");
422‚Üí        }
423‚Üí    }
424‚Üí    
425‚Üí    private void logCompletion(long startTime, String outputPath, long analysisDuration) {
426‚Üí        long totalTime = System.currentTimeMillis() - startTime;
427‚Üí        
428‚Üí        // Calculate analysis percentage of total time
429‚Üí        double analysisPercentage = (analysisDuration * 100.0) / totalTime;
430‚Üí        
431‚Üí        // Always show comprehensive timing information
432‚Üí        getLog().info("‚úÖ Maven analysis completed successfully!");
433‚Üí        getLog().info("üìä Total execution time: " + String.format("%.2f", totalTime / 1000.0) + "s");
434‚Üí        getLog().info("‚ö° Core analysis time: " + String.format("%.2f", analysisDuration / 1000.0) + "s (" + 
435‚Üí                     String.format("%.1f", analysisPercentage) + "% of total)");
436‚Üí        getLog().info("üìÅ Output written to: " + outputPath);
437‚Üí        getLog().info("üèóÔ∏è  Projects analyzed: " + reactorProjects.size());
438‚Üí        
439‚Üí        // Performance insights
440‚Üí        if (reactorProjects.size() > 0) {
441‚Üí            double avgTimePerProject = analysisDuration / (double) reactorProjects.size();
442‚Üí            getLog().info("‚è±Ô∏è  Average time per project: " + String.format("%.1f", avgTimePerProject) + "ms");
443‚Üí            
444‚Üí            if (avgTimePerProject > 1000) {
445‚Üí                getLog().warn("‚ö†Ô∏è  High average time per project detected - consider optimizing large projects");
446‚Üí            } else if (avgTimePerProject < 10) {
447‚Üí                getLog().info("üöÄ Excellent performance - very fast analysis per project");
448‚Üí            }
449‚Üí        }
450‚Üí    }
451‚Üí}

