        boolean fromParam = "true".equalsIgnoreCase(verboseStr);
        boolean fromSystem = "true".equalsIgnoreCase(systemProp);
        return fromParam || fromSystem;
    }

    private String determineOutputPath() {
        if (outputFile != null && !outputFile.isEmpty()) {
