        // Always include POM
        inputs.add(projectRootToken + "/pom.xml");

        // Get dynamic analysis of goal behavior
        GoalBehavior behavior = dynamicGoalAnalysis.analyzeGoal(goal, project);

        if (behavior.processesSources()) {
