  1	import {
  2	    createNodesFromFiles,
  3	    readJsonFile,
  4	    writeJsonFile,
  5	    ProjectConfiguration,
  6	    TargetConfiguration,
  7	    CreateNodesFunction,
  8	    CreateNodesContext,
  9	} from '@nx/devkit';
 10	import { calculateHashForCreateNodes } from '@nx/devkit/src/utils/calculate-hash-for-create-nodes';
 11	import { existsSync } from 'node:fs';
 12	import { dirname, join } from 'node:path';
 13	import { workspaceDataDirectory } from 'nx/src/utils/cache-directory';
 14	import { hashObject } from 'nx/src/devkit-internals';
 15	import { parsePomXml, MavenReport, MavenProjectInfo, MavenPlugin } from './parse-pom-xml';
 16	// import { MavenReport } from '../utils/get-maven-report';
 17	
 18	const MAVEN_CONFIG_GLOB = '**/pom.xml';
 19	const MAVEN_CONFIG_AND_TEST_GLOB = '{**/pom.xml,**/src/test/**/*.java}';
 20	
 21	interface MavenPluginOptions {
 22	    includeSubmodules?: boolean;
 23	    verifyTargetName?: string;
 24	    runTargetName?: string;
 25	    testTargetName?: string;
 26	    compileTargetName?: string;
 27	    packageTargetName?: string;
 28	    installTargetName?: string;
 29	}
 30	
 31	function normalizeOptions(options: MavenPluginOptions): MavenPluginOptions {
 32	    options ??= {};
 33	    options.verifyTargetName ??= 'verify';
 34	    options.runTargetName ??= 'run';
 35	    options.testTargetName ??= 'test';
 36	    options.compileTargetName ??= 'compile';
 37	    options.packageTargetName ??= 'package';
 38	    options.installTargetName ??= 'install';
 39	    return options;
 40	}
 41	
 42	type MavenTargets = Record<string, Partial<ProjectConfiguration>>;
 43	
 44	function readTargetsCache(cachePath: string): MavenTargets {
 45	    return existsSync(cachePath) ? readJsonFile(cachePath) : {};
 46	}
 47	
 48	function writeTargetsToCache(cachePath: string, results: MavenTargets) {
 49	    writeJsonFile(cachePath, results);
 50	}
 51	
 52	export const createNodesV2 = [
 53	    MAVEN_CONFIG_AND_TEST_GLOB,
 54	    async (files, options, context) => {
 55	        const { pomFiles, testFiles } = splitConfigFiles(files);
 56	        const optionsHash = hashObject(options);
 57	        const cachePath = join(workspaceDataDirectory, `maven-${optionsHash}.hash`);
 58	        const targetsCache = readTargetsCache(cachePath);
 59	
 60	        const mavenReport = await parsePomXml(context.workspaceRoot, pomFiles);
 61	        const mavenProjectRootToTestFilesMap = getMavenProjectRootToTestFilesMap(
 62	            testFiles,
 63	            pomFiles.map((f) => dirname(f))
 64	        );
 65	
 66	        try {
 67	            return createNodesFromFiles(
 68	                makeCreateNodesForMavenConfigFile(
 69	                    mavenReport,
 70	                    targetsCache,
 71	                    mavenProjectRootToTestFilesMap
 72	                ),
 73	                pomFiles,
 74	                options,
 75	                context
 76	            );
 77	        } finally {
 78	            writeTargetsToCache(cachePath, targetsCache);
 79	        }
 80	    },
 81	];
 82	
 83	export const makeCreateNodesForMavenConfigFile =
 84	    (
 85	        mavenReport: MavenReport,
 86	        targetsCache: MavenTargets = {},
 87	        mavenProjectRootToTestFilesMap: Record<string, string[]> = {}
 88	    ): CreateNodesFunction =>
 89	        async (
 90	            pomFilePath,
 91	            options: MavenPluginOptions | undefined,
 92	            context: CreateNodesContext
 93	        ) => {
 94	            const projectRoot = dirname(pomFilePath);
 95	            options = normalizeOptions(options);
 96	
 97	            const hash = await calculateHashForCreateNodes(
 98	                projectRoot,
 99	                options ?? {},
100	                context
101	            );
102	            targetsCache[hash] ??= await createMavenProject(
103	                mavenReport,
104	                pomFilePath,
105	                options,
106	                context,
107	                mavenProjectRootToTestFilesMap[projectRoot]
108	            );
109	            const project = targetsCache[hash];
110	            if (!project) {
111	                return {};
112	            }
113	            return {
114	                projects: {
115	                    [projectRoot]: project,
116	                },
117	            };
118	        };
119	
120	async function createMavenProject(
121	    mavenReport: MavenReport,
122	    pomFilePath: string,
123	    options: MavenPluginOptions | undefined,
124	    context: CreateNodesContext,
125	    testFiles: string[] = []
126	) {
127	    try {
128	        const projectInfo = mavenReport.projects.get(pomFilePath);
129	        if (!projectInfo) {
130	            return;
131	        }
132	
133	        const { targets, targetGroups } = await createMavenTargets(
134	            projectInfo,
135	            options,
136	            context,
137	            pomFilePath,
138	            testFiles
139	        );
140	
141	        const project: Partial<ProjectConfiguration> = {
142	            name: `${projectInfo.groupId ?? projectInfo.parent.groupId}.${
143	                projectInfo.artifactId
144	            }`,
145	            projectType: 'application',
146	            targets,
147	            metadata: {
148	                targetGroups,
149	                technologies: ['maven'],
150	            },
151	        };
152	
153	        return project;
154	    } catch (e) {
155	        console.error(e);
156	        return undefined;
157	    }
158	}
159	
160	async function createMavenTargets(
161	    projectInfo: MavenProjectInfo,
162	    options: MavenPluginOptions | undefined,
163	    context: CreateNodesContext,
164	    pomFilePath: string,
165	    testFiles: string[] = []
166	): Promise<{
167	    targetGroups: Record<string, string[]>;
168	    targets: Record<string, TargetConfiguration>;
169	}> {
170	    const targets: Record<string, TargetConfiguration> = {};
171	    const targetGroups: Record<string, string[]> = {
172	        build: [],
173	        test: [],
174	        dev: [],
175	        quality: [],
176	    };
177	
178	    const projectRoot = dirname(pomFilePath);
179	    const hasTests = testFiles.length > 0;
180	    const isQuarkusProject = hasQuarkusPlugin(projectInfo);
181	    const isSpringBootProject = hasSpringBootPlugin(projectInfo);
182	
183	    // Create enhanced inputs/outputs based on project structure
184	    const commonInputs = getProjectInputs(projectInfo, projectRoot);
185	    const commonOutputs = getProjectOutputs(projectInfo, projectRoot);
186	
187	    // Add standard Maven lifecycle targets with enhanced configuration
188	    const lifecycleTargets = [
189	        {
190	            name: options.compileTargetName,
191	            phase: 'compile',
192	            group: 'build',
193	            outputs: ['{projectRoot}/target/classes'],
194	        },
195	        {
196	            name: options.testTargetName,
197	            phase: 'test',
198	            group: 'test',
199	            outputs: hasTests ? ['{projectRoot}/target/test-results', '{projectRoot}/target/surefire-reports'] : undefined,
200	            condition: hasTests,
201	        },
202	        {
203	            name: options.packageTargetName,
204	            phase: 'package',
205	            group: 'build',
206	            outputs: [`{projectRoot}/target/*.${projectInfo.packaging || 'jar'}`],
207	        },
208	        {
209	            name: options.verifyTargetName,
210	            phase: 'verify',
211	            group: 'quality',
212	            outputs: undefined,
213	        },
214	        {
215	            name: options.installTargetName,
216	            phase: 'install',
217	            group: 'build',
218	            outputs: undefined,
219	        },
220	    ];
221	
222	    // Add lifecycle targets
223	    for (const target of lifecycleTargets) {
224	        if (target.condition === false) continue;
225	
226	        const targetConfig: TargetConfiguration = {
227	            command: `mvn ${target.phase}`,
228	            options: {
229	                cwd: projectRoot,
230	            },
231	            cache: true,
232	            inputs: commonInputs,
233	            outputs: target.outputs,
234	            metadata: {
235	                technologies: ['maven'],
236	                description: `Run Maven ${target.phase} phase`,
237	            },
238	        };
239	
240	        targets[target.name] = targetConfig;
241	        targetGroups[target.group].push(target.name);
242	    }
243	
244	    // Add framework-specific targets
245	    if (isQuarkusProject) {
246	        addQuarkusTargets(targets, targetGroups, projectRoot, options);
247	    }
248	
249	    if (isSpringBootProject) {
250	        addSpringBootTargets(targets, targetGroups, projectRoot, options);
251	    }
252	
253	    // Add plugin-specific targets based on Maven plugins
254	    addPluginSpecificTargets(projectInfo, targets, targetGroups, projectRoot, options);
255	
256	    return { targets, targetGroups };
257	}
258	
259	function hasQuarkusPlugin(projectInfo: MavenProjectInfo): boolean {
260	    return projectInfo.plugins?.some(
261	        plugin => plugin.groupId === 'io.quarkus' && plugin.artifactId === 'quarkus-maven-plugin'
262	    ) || false;
263	}
264	
265	function hasSpringBootPlugin(projectInfo: MavenProjectInfo): boolean {
266	    return projectInfo.plugins?.some(
267	        plugin => plugin.groupId === 'org.springframework.boot' && plugin.artifactId === 'spring-boot-maven-plugin'
268	    ) || false;
269	}
270	
271	function getProjectInputs(projectInfo: MavenProjectInfo, projectRoot: string): string[] {
272	    const inputs = ['default', '^production'];
273	    
274	    // Add source directories
275	    inputs.push('{projectRoot}/src/main/**/*');
276	    
277	    // Add test directories if they exist
278	    inputs.push('{projectRoot}/src/test/**/*');
279	    
280	    // Add resources
281	    inputs.push('{projectRoot}/src/main/resources/**/*');
282	    
283	    return inputs;
284	}
285	
286	function getProjectOutputs(projectInfo: MavenProjectInfo, projectRoot: string): string[] {
287	    return [
288	        '{projectRoot}/target/**/*',
289	    ];
290	}
291	
292	function addQuarkusTargets(
293	    targets: Record<string, TargetConfiguration>,
294	    targetGroups: Record<string, string[]>,
295	    projectRoot: string,
296	    options: MavenPluginOptions | undefined
297	): void {
298	    // Quarkus dev mode
299	    targets['dev'] = {
300	        command: 'mvn quarkus:dev',
301	        options: { cwd: projectRoot },
302	        cache: false,
303	        inputs: ['{projectRoot}/src/**/*', '{projectRoot}/pom.xml'],
304	        metadata: {
305	            technologies: ['maven', 'quarkus'],
306	            description: 'Start Quarkus in development mode',
307	        },
308	    };
309	    targetGroups.dev.push('dev');
310	
311	    // Quarkus native build
312	    targets['build-native'] = {
313	        command: 'mvn package -Dnative',
314	        options: { cwd: projectRoot },
315	        cache: true,
316	        inputs: ['{projectRoot}/src/**/*', '{projectRoot}/pom.xml'],
317	        outputs: ['{projectRoot}/target/*-runner'],
318	        metadata: {
319	            technologies: ['maven', 'quarkus'],
320	            description: 'Build native executable with Quarkus',
321	        },
322	    };
323	    targetGroups.build.push('build-native');
324	}
325	
326	function addSpringBootTargets(
327	    targets: Record<string, TargetConfiguration>,
328	    targetGroups: Record<string, string[]>,
329	    projectRoot: string,
330	    options: MavenPluginOptions | undefined
331	): void {
332	    // Spring Boot run
333	    targets['run'] = {
334	        command: 'mvn spring-boot:run',
335	        options: { cwd: projectRoot },
336	        cache: false,
337	        inputs: ['{projectRoot}/src/**/*', '{projectRoot}/pom.xml'],
338	        metadata: {
339	            technologies: ['maven', 'spring-boot'],
340	            description: 'Run Spring Boot application',
341	        },
342	    };
343	    targetGroups.dev.push('run');
344	}
345	
346	function addPluginSpecificTargets(
347	    projectInfo: MavenProjectInfo,
348	    targets: Record<string, TargetConfiguration>,
349	    targetGroups: Record<string, string[]>,
350	    projectRoot: string,
351	    options: MavenPluginOptions | undefined
352	): void {
353	    if (!projectInfo.plugins) return;
354	
355	    for (const plugin of projectInfo.plugins) {
356	        // Flyway plugin
357	        if (plugin.groupId === 'org.flywaydb' && plugin.artifactId === 'flyway-maven-plugin') {
358	            targets['db-migrate'] = {
359	                command: 'mvn flyway:migrate',
360	                options: { cwd: projectRoot },
361	                cache: false,
362	                inputs: ['{projectRoot}/src/main/resources/db/migration/**/*'],
363	                metadata: {
364	                    technologies: ['maven', 'flyway'],
365	                    description: 'Run database migrations',
366	                },
367	            };
368	            targetGroups.build.push('db-migrate');
369	        }
370	
371	        // Liquibase plugin
372	        if (plugin.groupId === 'org.liquibase' && plugin.artifactId === 'liquibase-maven-plugin') {
373	            targets['db-update'] = {
374	                command: 'mvn liquibase:update',
375	                options: { cwd: projectRoot },
376	                cache: false,
377	                inputs: ['{projectRoot}/src/main/resources/db/changelog/**/*'],
378	                metadata: {
379	                    technologies: ['maven', 'liquibase'],
380	                    description: 'Update database schema',
381	                },
382	            };
383	            targetGroups.build.push('db-update');
384	        }
385	
386	        // Spotless plugin for code formatting
387	        if (plugin.groupId === 'com.diffplug.spotless' && plugin.artifactId === 'spotless-maven-plugin') {
388	            targets['format'] = {
389	                command: 'mvn spotless:apply',
390	                options: { cwd: projectRoot },
391	                cache: false,
392	                inputs: ['{projectRoot}/src/**/*'],
393	                metadata: {
394	                    technologies: ['maven', 'spotless'],
395	                    description: 'Format code with Spotless',
396	                },
397	            };
398	            targetGroups.quality.push('format');
399	        }
400	
401	        // Maven Checkstyle plugin
402	        if (plugin.groupId === 'org.apache.maven.plugins' && plugin.artifactId === 'maven-checkstyle-plugin') {
403	            targets['checkstyle'] = {
404	                command: 'mvn checkstyle:check',
405	                options: { cwd: projectRoot },
406	                cache: true,
407	                inputs: ['{projectRoot}/src/**/*'],
408	                outputs: ['{projectRoot}/target/checkstyle-result.xml'],
409	                metadata: {
410	                    technologies: ['maven', 'checkstyle'],
411	                    description: 'Run Checkstyle code analysis',
412	                },
413	            };
414	            targetGroups.quality.push('checkstyle');
415	        }
416	
417	        // SpotBugs plugin
418	        if (plugin.groupId === 'com.github.spotbugs' && plugin.artifactId === 'spotbugs-maven-plugin') {
419	            targets['spotbugs'] = {
420	                command: 'mvn spotbugs:check',
421	                options: { cwd: projectRoot },
422	                cache: true,
423	                inputs: ['{projectRoot}/src/**/*'],
424	                outputs: ['{projectRoot}/target/spotbugsXml.xml'],
425	                metadata: {
426	                    technologies: ['maven', 'spotbugs'],
427	                    description: 'Run SpotBugs static analysis',
428	                },
429	            };
430	            targetGroups.quality.push('spotbugs');
431	        }
432	    }
433	}
434	
435	function splitConfigFiles(files: readonly string[]) {
436	    const pomFiles = files
437	        .filter((f) => f.endsWith('pom.xml'))
438	        .filter((f) => !isTestOrTemplateDirectory(f));
439	    const testFiles = files.filter((f) => f.includes('/src/test/'));
440	    return { pomFiles, testFiles };
441	}
442	
443	function isTestOrTemplateDirectory(filePath: string): boolean {
444	    const excludePatterns = [
445	        '/src/test/resources',
446	        '/maven-archetype/',
447	        '/resources-filtered/',
448	        '/test/resources',
449	        'archetype-resources',
450	        '/templates/',
