  1import model.*;
  2import org.apache.maven.project.MavenProject;
  3import org.apache.maven.model.Dependency;
  4import org.apache.maven.plugin.logging.Log;
  5import java.io.File;
  6import java.util.*;
  7
  8/**
  9 * Generates CreateDependencies compatible results for Nx integration
 10 */
 11public class CreateDependenciesGenerator {
 12
 13    /**
 14     * Generate CreateDependencies results from Maven project list
 15     * Returns: List<RawProjectGraphDependency>
 16     */
 17    public static List<RawProjectGraphDependency> generateCreateDependencies(List<MavenProject> projects, File workspaceRoot) {
 18        return generateCreateDependencies(projects, workspaceRoot, null, false);
 19    }
 20
 21    /**
 22     * Generate CreateDependencies results with logging support
 23     */
 24    public static List<RawProjectGraphDependency> generateCreateDependencies(List<MavenProject> projects, File workspaceRoot, Log log, boolean verbose) {
 25        List<RawProjectGraphDependency> dependencies = new ArrayList<>();
 26
 27        // Extract workspace projects
 28        Map<String, String> artifactToProject = buildArtifactMapping(projects);
 29
 30        if (verbose && log != null) {
 31            log.info("Built artifact mapping for " + artifactToProject.size() + " workspace projects");
 32        }
 33
 34        int staticDeps = 0;
 35        int implicitDeps = 0;
 36
 37        for (int i = 0; i < projects.size(); i++) {
 38            MavenProject project = projects.get(i);
 39            String source = NxPathUtils.getProjectName(project);
 40            String sourceFile = NxPathUtils.getRelativePomPath(project, workspaceRoot);
 41
 42            log.info("Dependency analysis progress: " + (i + 1) + "/" + projects.size() + " projects");
 43
 44            // 1. Static dependencies - direct Maven dependencies
 45            int prevStatic = staticDeps;
 46            staticDeps += addStaticDependencies(dependencies, project, artifactToProject, sourceFile);
 47
 48            // 2. Implicit dependencies - parent/module relationships
 49            int prevImplicit = implicitDeps;
 50            implicitDeps += addImplicitDependencies(dependencies, project, projects, source);
 51
 52            if (verbose && log != null && projects.size() <= 20) {
 53                int newStatic = staticDeps - prevStatic;
 54                int newImplicit = implicitDeps - prevImplicit;
 55                if (newStatic > 0 || newImplicit > 0) {
 56                    log.info("Project " + source + ": " + newStatic + " static, " + newImplicit + " implicit dependencies");
 57                }
 58            }
 59        }
 60
 61        if (verbose && log != null) {
 62            log.info("Dependency analysis complete: " + staticDeps + " static, " + implicitDeps + " implicit dependencies");
 63        }
 64
 65        return dependencies;
 66    }
 67
 68    /**
 69     * Build mapping from artifactId to project name for workspace projects
 70     */
 71    private static Map<String, String> buildArtifactMapping(List<MavenProject> projects) {
 72        Map<String, String> mapping = new HashMap<>();
 73        for (MavenProject project : projects) {
 74            String projectName = NxPathUtils.getProjectName(project);
 75            if (project.getGroupId() != null && project.getArtifactId() != null) {
 76                String key = project.getGroupId() + ":" + project.getArtifactId();
 77                mapping.put(key, projectName);
 78            }
 79        }
 80        return mapping;
 81    }
 82
 83    /**
 84     * Add static dependencies (explicit Maven dependencies between workspace projects)
 85     * Returns: number of dependencies added
 86     */
 87    private static int addStaticDependencies(List<RawProjectGraphDependency> dependencies,
 88                                              MavenProject project,
 89                                              Map<String, String> artifactToProject,
 90                                              String sourceFile) {
 91        String source = NxPathUtils.getProjectName(project);
 92        int count = 0;
 93
 94        // Check both declared dependencies and resolved artifacts
 95        if (project.getDependencies() != null) {
 96            for (Dependency dep : project.getDependencies()) {
 97                if (dep.getGroupId() != null && dep.getArtifactId() != null) {
 98                    String depKey = dep.getGroupId() + ":" + dep.getArtifactId();
 99
100                    // Check if this dependency refers to another project in workspace
101                    String target = artifactToProject.get(depKey);
102                    if (target != null && !target.equals(source)) {
103                        RawProjectGraphDependency dependency = new RawProjectGraphDependency(
104                            source, target, RawProjectGraphDependency.DependencyType.STATIC, sourceFile);
105                        dependencies.add(dependency);
106                        count++;
107                    }
108                }
109            }
110        }
111
112        return count;
113    }
114
115    /**
116     * Add implicit dependencies (parent-child module relationships)
117     * Returns: number of dependencies added
118     */
119    private static int addImplicitDependencies(List<RawProjectGraphDependency> dependencies,
120                                                MavenProject project,
121                                                List<MavenProject> allProjects,
122                                                String source) {
123        // Find parent-child relationships
124        File projectDir = project.getBasedir();
125        int count = 0;
126
127        for (MavenProject otherProject : allProjects) {
128            File otherDir = otherProject.getBasedir();
129            String target = NxPathUtils.getProjectName(otherProject);
130
131            if (!source.equals(target)) {
132                // Check if one is parent of the other
133                if (NxPathUtils.isParentChildRelation(projectDir, otherDir)) {
134                    RawProjectGraphDependency dependency = new RawProjectGraphDependency(
135                        source, target, RawProjectGraphDependency.DependencyType.IMPLICIT);
136                    dependencies.add(dependency);
137                    count++;
138                }
139            }
140        }
141
142        return count;
143    }
144
145
146    /**
147     * Check if a dependency is a test-scoped dependency
148     */
149    private static boolean isTestDependency(Dependency dep) {
150        return "test".equals(dep.getScope());
151    }
152
153    /**
154     * Check if a dependency is optional
155     */
156    private static boolean isOptionalDependency(Dependency dep) {
157        return dep.isOptional();
158    }
159}
