  1import org.apache.maven.execution.MavenSession;
  2import org.apache.maven.lifecycle.LifecycleExecutor;
  3import org.apache.maven.lifecycle.MavenExecutionPlan;
  4import org.apache.maven.plugin.AbstractMojo;
  5import org.apache.maven.plugin.MojoExecutionException;
  6import org.apache.maven.plugin.MojoFailureException;
  7import org.apache.maven.plugin.MojoExecution;
  8import org.apache.maven.plugins.annotations.Component;
  9import org.apache.maven.plugins.annotations.Mojo;
 10import org.apache.maven.plugins.annotations.Parameter;
 11import org.apache.maven.plugins.annotations.ResolutionScope;
 12import org.apache.maven.project.MavenProject;
 13
 14import com.google.gson.Gson;
 15import com.google.gson.GsonBuilder;
 16
 17import model.*;
 18import java.io.File;
 19import java.io.FileWriter;
 20import java.io.IOException;
 21import java.util.*;
 22
 23/**
 24 * Maven plugin that uses MavenSession API to analyze Maven projects for Nx integration
 25 */
 26@Mojo(name = "analyze", aggregator = true, requiresDependencyResolution = ResolutionScope.COMPILE_PLUS_RUNTIME)
 27public class NxAnalyzerMojo extends AbstractMojo {
 28    
 29    @Parameter(defaultValue = "${session}", readonly = true, required = true)
 30    private MavenSession session;
 31    
 32    @Parameter(defaultValue = "${reactorProjects}", readonly = true, required = true)
 33    private List<MavenProject> reactorProjects;
 34    
 35    @Component
 36    private LifecycleExecutor lifecycleExecutor;
 37    
 38    @Parameter(property = "nx.outputFile")
 39    private String outputFile;
 40    
 41    @Parameter(property = "nx.verbose", defaultValue = "false")
 42    private String verboseStr;
 43    
 44    private boolean isVerbose() {
 45        // Check both the parameter and system property
 46        String systemProp = System.getProperty("nx.verbose");
 47        boolean fromParam = "true".equalsIgnoreCase(verboseStr);
 48        boolean fromSystem = "true".equalsIgnoreCase(systemProp);
 49        return fromParam || fromSystem;
 50    }
 51    
 52    @Override
 53    public void execute() throws MojoExecutionException, MojoFailureException {
 54        long startTime = System.currentTimeMillis();
 55        
 56        try {
 57            getLog().info("Starting Nx analysis using MavenSession with dependency resolution...");
 58            String systemProp = System.getProperty("nx.verbose");
 59            getLog().info("Debug: Verbose param='" + verboseStr + "' system='" + systemProp + "' -> " + isVerbose());
 60            
 61            // Use reactor projects from Maven session - these are the properly resolved projects
 62            List<MavenProject> projects = reactorProjects;
 63            getLog().info("Found " + projects.size() + " projects in reactor");
 64            
 65            if (isVerbose()) {
 66                getLog().info("Verbose mode enabled - detailed progress will be shown");
 67            }
 68            
 69            // Always show some basic details - the condition was checking verbose but should always show
 70            getLog().info("Root directory: " + session.getExecutionRootDirectory());
 71            if (projects.size() > 0) {
 72                getLog().info("First project: " + projects.get(0).getGroupId() + ":" + projects.get(0).getArtifactId());
 73                getLog().info("Last project: " + projects.get(projects.size()-1).getGroupId() + ":" + projects.get(projects.size()-1).getArtifactId());
 74            }
 75            
 76            // Convert Maven projects to Nx format
 77            Map<String, Object> result = convertToNxFormat(projects);
 78            
 79            // Write output
 80            String outputPath = determineOutputPath();
 81            if (isVerbose()) {
 82                getLog().info("Writing results to: " + outputPath);
 83            }
 84            writeResult(result, outputPath);
 85            
 86            long totalTime = System.currentTimeMillis() - startTime;
 87            double projectsPerSecond = projects.size() / (totalTime / 1000.0);
 88            
 89            getLog().info("Analysis complete. Results written to: " + outputPath);
 90            if (isVerbose()) {
 91                getLog().info("Performance: Processed " + projects.size() + " projects in " + 
 92                             (totalTime / 1000.0) + "s (" + String.format("%.1f", projectsPerSecond) + " projects/sec)");
 93            }
 94            getLog().info("SUCCESS: Maven analysis completed successfully");
 95            
 96        } catch (Exception e) {
 97            throw new MojoExecutionException("Analysis failed: " + e.getMessage(), e);
 98        }
 99    }
100    
101    private Map<String, Object> convertToNxFormat(List<MavenProject> projects) {
102        long startTime = System.currentTimeMillis();
103        
104        if (isVerbose()) {
105            getLog().info("Target generation phase: Processing " + projects.size() + " projects...");
106        }
107        
108        // Generate targets for each project
109        Map<MavenProject, Map<String, TargetConfiguration>> projectTargets = new LinkedHashMap<>();
110        Map<MavenProject, Map<String, TargetGroup>> projectTargetGroups = new LinkedHashMap<>();
111        int totalTargets = 0;
112        
113        for (int i = 0; i < projects.size(); i++) {
114            MavenProject project = projects.get(i);
115            
116            if (isVerbose() && projects.size() > 50) {
117                // Show progress for large workspaces
118                if (i % 50 == 0 || i == projects.size() - 1) {
119                    double percentage = (i + 1) * 100.0 / projects.size();
120                    long elapsed = System.currentTimeMillis() - startTime;
121                    double rate = (i + 1) / (elapsed / 1000.0);
122                    long eta = (long) ((projects.size() - i - 1) / rate);
123                    
124                    getLog().info("Target generation progress: " + (i + 1) + "/" + projects.size() + 
125                                 " (" + String.format("%.1f", percentage) + "%) - " +
126                                 String.format("%.1f", rate) + " projects/sec - ETA: " + eta + "s");
127                }
128            } else if (isVerbose()) {
129                // Show individual project progress for smaller workspaces
130                getLog().info("Processing project " + (i + 1) + "/" + projects.size() + 
131                             ": " + project.getGroupId() + ":" + project.getArtifactId());
132            }
133            
134            Map<String, TargetConfiguration> targets = generateTargets(project);
135            projectTargets.put(project, targets);
136            totalTargets += targets.size();
137            
138            // Generate target groups for this project
139            if (isVerbose()) {
140                getLog().info("About to generate target groups for " + project.getArtifactId());
141            }
142            try {
143                Map<String, TargetGroup> targetGroups = generateTargetGroups(project, targets);
144                projectTargetGroups.put(project, targetGroups);
145                
146                if (isVerbose()) {
147                    getLog().info("Generated " + targetGroups.size() + " target groups for " + project.getArtifactId());
148                    for (Map.Entry<String, TargetGroup> entry : targetGroups.entrySet()) {
149                        TargetGroup group = entry.getValue();
150                        getLog().info("  Group '" + entry.getKey() + "': " + group.getTargets().size() + " targets");
151                    }
152                }
153            } catch (Exception e) {
154                getLog().error("Error generating target groups for " + project.getArtifactId() + ": " + e.getMessage(), e);
155                // Continue with empty target groups
156                projectTargetGroups.put(project, new LinkedHashMap<>());
157            }
158        }
159        
160        long targetGenTime = System.currentTimeMillis() - startTime;
161        if (isVerbose()) {
162            getLog().info("Target generation complete: " + totalTargets + " targets created in " + 
163                         (targetGenTime / 1000.0) + "s");
164        }
165        
166        // Generate Nx-compatible outputs
167        File workspaceRoot = new File(session.getExecutionRootDirectory());
168        
169        if (isVerbose()) {
170            getLog().info("Generating CreateNodes results for " + projects.size() + " projects...");
171        }
172        long createNodesStart = System.currentTimeMillis();
173        List<CreateNodesV2Entry> createNodesEntries = CreateNodesResultGenerator.generateCreateNodesV2Results(projects, workspaceRoot, projectTargets, projectTargetGroups);
174        List<Object[]> createNodesResults = new ArrayList<>();
175        for (CreateNodesV2Entry entry : createNodesEntries) {
176            createNodesResults.add(entry.toArray());
177        }
178        long createNodesTime = System.currentTimeMillis() - createNodesStart;
179        
180        if (isVerbose()) {
181            getLog().info("Generating dependency graph for " + projects.size() + " projects...");
182        }
183        long dependenciesStart = System.currentTimeMillis();
184        List<RawProjectGraphDependency> createDependencies = CreateDependenciesGenerator.generateCreateDependencies(projects, workspaceRoot, getLog(), isVerbose());
185        long dependenciesTime = System.currentTimeMillis() - dependenciesStart;
186        
187        if (isVerbose()) {
188            getLog().info("CreateNodes generation: " + (createNodesTime / 1000.0) + "s");
189            getLog().info("Dependencies generation: " + (dependenciesTime / 1000.0) + "s");
190            getLog().info("Found " + createDependencies.size() + " workspace dependencies");
191        }
192        
193        Map<String, Object> result = new LinkedHashMap<>();
194        result.put("createNodesResults", createNodesResults);
195        result.put("createDependencies", createDependencies);
196        
197        return result;
198    }
