221    private Map<String, TargetConfiguration> generatePhaseTargets(MavenProject project, File workspaceRoot) {
222        Map<String, TargetConfiguration> phaseTargets = new LinkedHashMap<>();
223        
224        String[] phases = {
225            "clean", "validate", "compile", "test", "package", 
226            "verify", "install", "deploy", "site"
227        };
228        
229        String projectRootToken = "{projectRoot}";
230        if (workspaceRoot != null) {
231            String relativePath = NxPathUtils.getRelativePath(workspaceRoot, project.getBasedir());
232            projectRootToken = relativePath.isEmpty() ? "." : relativePath;
233        }
234        
235        for (String phase : phases) {
236            TargetConfiguration target = new TargetConfiguration("@nx/run-commands:run-commands");
237            
238            Map<String, Object> options = new LinkedHashMap<>();
239            options.put("command", "mvn " + phase);
240            options.put("cwd", projectRootToken);
241            target.setOptions(options);
242            
243            // Add inputs based on phase
244            List<String> inputs = new ArrayList<>();
245            inputs.add(projectRootToken + "/pom.xml");
246            if (needsSourceInputs(phase)) {
247                inputs.add(projectRootToken + "/src/**/*");
248            }
249            target.setInputs(inputs);
250            
251            // Add outputs based on phase
252            List<String> outputs = getPhaseOutputs(phase, projectRootToken);
253            target.setOutputs(outputs);
254            
255            // Add phase dependencies
256            List<String> dependsOn = getPhaseDependencies(phase);
257            target.setDependsOn(dependsOn);
258            
259            // Add metadata
260            TargetMetadata metadata = new TargetMetadata("phase", "Maven lifecycle phase: " + phase);
261            metadata.setPhase(phase);
262            metadata.setTechnologies(Arrays.asList("maven"));
263            target.setMetadata(metadata);
264            
265            phaseTargets.put(phase, target);
266        }
267        
268        return phaseTargets;
269    }
