275    private Map<String, TargetConfiguration> generatePluginGoalTargets(MavenProject project, File workspaceRoot) {
276        Map<String, TargetConfiguration> goalTargets = new LinkedHashMap<>();
277        
278        final String projectRootToken;
279        if (workspaceRoot != null) {
280            String relativePath = NxPathUtils.getRelativePath(workspaceRoot, project.getBasedir());
281            projectRootToken = relativePath.isEmpty() ? "." : relativePath;
282        } else {
283            projectRootToken = "{projectRoot}";
284        }
285        
286        if (project.getBuildPlugins() != null) {
287            project.getBuildPlugins().forEach(plugin -> {
288                String pluginKey = plugin.getGroupId() + ":" + plugin.getArtifactId();
289                
290                // Process actual executions from effective POM
291                if (plugin.getExecutions() != null) {
292                    plugin.getExecutions().forEach(execution -> {
293                        if (execution.getGoals() != null) {
294                            execution.getGoals().forEach(goal -> {
295                                String targetName = getTargetName(plugin.getArtifactId(), goal);
296                                
297                                if (!goalTargets.containsKey(targetName)) {
298                                    TargetConfiguration target = createGoalTarget(plugin, goal, execution, projectRootToken);
299                                    goalTargets.put(targetName, target);
300                                }
301                            });
302                        }
303                    });
304                }
305                
306                // Add common goals for well-known plugins
307                addCommonGoalsForPlugin(plugin, goalTargets, projectRootToken);
308            });
309        }
310        
311        return goalTargets;
312    }
