314    private TargetConfiguration createGoalTarget(org.apache.maven.model.Plugin plugin, String goal, 
315                                                 org.apache.maven.model.PluginExecution execution, String projectRootToken) {
316        String pluginKey = plugin.getGroupId() + ":" + plugin.getArtifactId();
317        
318        TargetConfiguration target = new TargetConfiguration("@nx/run-commands:run-commands");
319        
320        Map<String, Object> options = new LinkedHashMap<>();
321        options.put("command", "mvn " + pluginKey + ":" + goal);
322        options.put("cwd", projectRootToken);
323        target.setOptions(options);
324        
325        // Smart inputs/outputs based on goal
326        List<String> inputs = new ArrayList<>();
327        inputs.add(projectRootToken + "/pom.xml");
328        if (isSourceProcessingGoal(goal)) {
329            inputs.add(projectRootToken + "/src/**/*");
330        }
331        target.setInputs(inputs);
332        
333        List<String> outputs = getGoalOutputs(goal, projectRootToken);
334        target.setOutputs(outputs);
335        
336        // Metadata with execution info
337        TargetMetadata metadata = new TargetMetadata("goal", generateGoalDescription(plugin.getArtifactId(), goal));
338        metadata.setPlugin(pluginKey);
339        metadata.setGoal(goal);
340        metadata.setExecutionId(execution.getId());
341        metadata.setPhase(execution.getPhase());
342        metadata.setTechnologies(Arrays.asList("maven"));
343        target.setMetadata(metadata);
344        
345        return target;
346    }
