348    private void addCommonGoalsForPlugin(org.apache.maven.model.Plugin plugin, Map<String, TargetConfiguration> goalTargets, String projectRootToken) {
349        String artifactId = plugin.getArtifactId();
350        List<String> commonGoals = new ArrayList<>();
351        
352        if (artifactId.contains("compiler")) {
353            commonGoals.addAll(Arrays.asList("compile", "testCompile"));
354        } else if (artifactId.contains("surefire")) {
355            commonGoals.add("test");
356        } else if (artifactId.contains("quarkus")) {
357            commonGoals.addAll(Arrays.asList("dev", "build"));
358        } else if (artifactId.contains("spring-boot")) {
359            commonGoals.addAll(Arrays.asList("run", "repackage"));
360        }
361        
362        for (String goal : commonGoals) {
363            String targetName = getTargetName(artifactId, goal);
364            if (!goalTargets.containsKey(targetName)) {
365                TargetConfiguration target = createSimpleGoalTarget(plugin, goal, projectRootToken);
366                goalTargets.put(targetName, target);
367            }
368        }
369    }
