405    private List<String> getPhaseOutputs(String phase, String projectRootToken) {
406        List<String> outputs = new ArrayList<>();
407        switch (phase) {
408            case "compile":
409                outputs.add(projectRootToken + "/target/classes/**/*");
410                break;
411            case "test":
412                outputs.add(projectRootToken + "/target/surefire-reports/**/*");
413                outputs.add(projectRootToken + "/target/test-classes/**/*");
414                break;
415            case "package":
416            case "verify":
417            case "install":
418            case "deploy":
419                outputs.add(projectRootToken + "/target/*.jar");
420                outputs.add(projectRootToken + "/target/*.war");
421                break;
422            case "site":
423                outputs.add(projectRootToken + "/target/site/**/*");
424                break;
