456    private List<String> getGoalOutputs(String goal, String projectRootToken) {
457        List<String> outputs = new ArrayList<>();
458        if (goal.contains("compile") || goal.contains("build") || goal.contains("jar") || goal.contains("war")) {
459            outputs.add(projectRootToken + "/target/**/*");
460        } else if (goal.contains("test")) {
461            outputs.add(projectRootToken + "/target/surefire-reports/**/*");
462            outputs.add(projectRootToken + "/target/failsafe-reports/**/*");
463        } else if (goal.contains("site") || goal.contains("javadoc")) {
464            outputs.add(projectRootToken + "/target/site/**/*");
465        }
