474    private String generateGoalDescription(String artifactId, String goal) {
475        String pluginName = artifactId.replace("-maven-plugin", "").replace("-plugin", "");
476        
477        switch (goal) {
478            case "compile": return "Compile main sources";
479            case "testCompile": return "Compile test sources";
480            case "test": return "Run tests";
481            case "integration-test": return "Run integration tests";
482            case "dev": return "Start development mode";
483            case "run": return "Run application";
484            case "build": return "Build application";
485            case "jar": return "Create JAR";
486            case "war": return "Create WAR";
487            case "site": return "Generate site documentation";
488            case "javadoc": return "Generate Javadoc";
489            case "enforce": return "Enforce build rules";
490            case "create": return "Create build metadata";
491            default: return pluginName + " " + goal;
492        }
493    }
