495    private Map<String, TargetGroup> generateTargetGroups(MavenProject project, Map<String, TargetConfiguration> projectTargets) {
496        Map<String, TargetGroup> targetGroups = new LinkedHashMap<>();
497        
498        // Define Maven lifecycle phases with their order
499        String[] phases = {
500            "clean", "validate", "compile", "test", "package", 
501            "verify", "install", "deploy", "site"
502        };
503        
504        Map<String, String> phaseDescriptions = new LinkedHashMap<>();
505        phaseDescriptions.put("clean", "Clean up artifacts created by build");
506        phaseDescriptions.put("validate", "Validate project structure and configuration");
507        phaseDescriptions.put("compile", "Compile source code");
508        phaseDescriptions.put("test", "Run unit tests");
509        phaseDescriptions.put("package", "Package compiled code");
510        phaseDescriptions.put("verify", "Verify package integrity");
511        phaseDescriptions.put("install", "Install package to local repository");
512        phaseDescriptions.put("deploy", "Deploy package to remote repository");
513        phaseDescriptions.put("site", "Generate project documentation");
514        
515        // Create target groups for each phase
516        for (int i = 0; i < phases.length; i++) {
517            String phase = phases[i];
518            TargetGroup group = new TargetGroup(phase, phaseDescriptions.get(phase), i);
519            targetGroups.put(phase, group);
520        }
521        
522        // Try to get execution plan to assign plugin goals to phases
523        try {
524            MavenExecutionPlan executionPlan = lifecycleExecutor.calculateExecutionPlan(session, "package");
525            
526            if (isVerbose()) {
527                getLog().info("Using execution plan to assign targets to phase groups");
528            }
529            
530            // Map plugin executions to phases
531            Map<String, String> goalToPhaseMap = new LinkedHashMap<>();
532            for (MojoExecution execution : executionPlan.getMojoExecutions()) {
533                String goal = execution.getGoal();
534                String phase = execution.getLifecyclePhase();
535                if (phase != null && !phase.isEmpty()) {
536                    goalToPhaseMap.put(goal, phase);
537                    
538                    // Also map plugin:goal format
539                    String pluginKey = execution.getPlugin().getGroupId() + ":" + execution.getPlugin().getArtifactId();
540                    String targetName = getTargetName(execution.getPlugin().getArtifactId(), goal);
541                    goalToPhaseMap.put(targetName, phase);
542                }
543            }
544            
545            // Assign targets to groups based on execution plan
546            for (String targetName : projectTargets.keySet()) {
547                TargetConfiguration target = projectTargets.get(targetName);
548                String assignedPhase = null;
549                
550                // Check if target metadata has phase info
551                if (target.getMetadata() != null && target.getMetadata().getPhase() != null) {
552                    assignedPhase = target.getMetadata().getPhase();
553                } else {
554                    // Try to match by goal name
555                    for (Map.Entry<String, String> entry : goalToPhaseMap.entrySet()) {
556                        if (targetName.contains(entry.getKey()) || targetName.equals(entry.getKey())) {
557                            assignedPhase = entry.getValue();
558                            break;
559                        }
560                    }
561                }
562                
563                // Default phase assignment for targets without execution plan info
564                if (assignedPhase == null) {
565                    if (Arrays.asList(phases).contains(targetName)) {
566                        assignedPhase = targetName; // Phase targets go to their own group
567                    } else {
568                        assignedPhase = "compile"; // Default fallback
569                    }
570                }
571                
572                // Add target to the appropriate group
573                TargetGroup group = targetGroups.get(assignedPhase);
574                if (group != null) {
575                    group.addTarget(targetName);
576                }
577            }
578            
579        } catch (Exception e) {
580            if (isVerbose()) {
581                getLog().warn("Could not get execution plan, using fallback target grouping: " + e.getMessage());
582            }
583            
584            // Fallback: assign targets based on naming patterns
585            for (String targetName : projectTargets.keySet()) {
586                String assignedPhase = "compile"; // Default
587                
588                // Assign based on target name patterns
589                if (Arrays.asList(phases).contains(targetName)) {
590                    assignedPhase = targetName;
591                } else if (targetName.contains("test")) {
592                    assignedPhase = "test";
593                } else if (targetName.contains("package") || targetName.contains("jar") || targetName.contains("war")) {
594                    assignedPhase = "package";
595                } else if (targetName.contains("clean")) {
596                    assignedPhase = "clean";
597                } else if (targetName.contains("site") || targetName.contains("javadoc")) {
598                    assignedPhase = "site";
599                }
600                
601                TargetGroup group = targetGroups.get(assignedPhase);
602                if (group != null) {
603                    group.addTarget(targetName);
604                }
605            }
606        }
607        
608        return targetGroups;
609    }
610    
611    private String determineOutputPath() {
612        if (outputFile != null && !outputFile.isEmpty()) {
613            return outputFile;
614        }
615        
616        // Default to workspace root
617        return new File(session.getExecutionRootDirectory(), "maven-analysis.json").getAbsolutePath();
618    }
