            for (String input : target.getInputs()) {
                if (input.startsWith("{projectRoot}/")) {
                    updatedInputs.add(input.replace("{projectRoot}", projectRootToken));
                } else {
                    updatedInputs.add(input);
                }
            }
            updatedTarget.setInputs(updatedInputs);

            // Update outputs
