private Map<String, Object> convertToNxFormat(List<MavenProject> projects) {
    long startTime = System.currentTimeMillis();

    if (isVerbose()) {
        getLog().info("Target generation phase: Processing " + projects.size() + " projects...");
    }

    // Generate targets for each project
    Map<MavenProject, Map<String, TargetConfiguration>> projectTargets = new LinkedHashMap<>();
    Map<MavenProject, Map<String, TargetGroup>> projectTargetGroups = new LinkedHashMap<>();
    int totalTargets = 0;

    for (int i = 0; i < projects.size(); i++) {
        MavenProject project = projects.get(i);

        if (isVerbose() && projects.size() > 50) {
            // Show progress for large workspaces
            if (i % 50 == 0 || i == projects.size() - 1) {
                double percentage = (i + 1) * 100.0 / projects.size();
                long elapsed = System.currentTimeMillis() - startTime;
                double rate = (i + 1) / (elapsed / 1000.0);
                long eta = (long) ((projects.size() - i - 1) / rate);

                getLog().info("Target generation progress: " + (i + 1) + "/" + projects.size() + 
                             " (" + String.format("%.1f", percentage) + "%) - " +
                             String.format("%.1f", rate) + " projects/sec - ETA: " + eta + "s");
            }
        } else if (isVerbose()) {
            // Show individual project progress for smaller workspaces
            getLog().info("Processing project " + (i + 1) + "/" + projects.size() + 
                         ": " + project.getGroupId() + ":" + project.getArtifactId());
        }

        Map<String, TargetConfiguration> targets = generateTargets(project);
        projectTargets.put(project, targets);
        totalTargets += targets.size();

        // Generate target groups for this project
        if (isVerbose()) {
            getLog().info("About to generate target groups for " + project.getArtifactId());
        }
        try {
            Map<String, TargetGroup> targetGroups = generateTargetGroups(project, targets);
            projectTargetGroups.put(project, targetGroups);

            if (isVerbose()) {
                getLog().info("Generated " + targetGroups.size() + " target groups for " + project.getArtifactId());
                for (Map.Entry<String, TargetGroup> entry : targetGroups.entrySet()) {
                    TargetGroup group = entry.getValue();
                    getLog().info("  Group '" + entry.getKey() + "': " + group.getTargets().size() + " targets");
                }
            }
        } catch (Exception e) {
            getLog().error("Error generating target groups for " + project.getArtifactId() + ": " + e.getMessage(), e);
            // Continue with empty target groups
            projectTargetGroups.put(project, new LinkedHashMap<>());
        }
    }

