    Map<String, TargetConfiguration> targets = generateTargets(project);
    projectTargets.put(project, targets);
    totalTargets += targets.size();

    // Generate target groups for this project
    if (isVerbose()) {
        getLog().info("About to generate target groups for " + project.getArtifactId());
    }
    try {
        Map<String, TargetGroup> targetGroups = generateTargetGroups(project, targets);
        projectTargetGroups.put(project, targetGroups);

        if (isVerbose()) {
            getLog().info("Generated " + targetGroups.size() + " target groups for " + project.getArtifactId());
            for (Map.Entry<String, TargetGroup> entry : targetGroups.entrySet()) {
                TargetGroup group = entry.getValue();
                getLog().info("  Group '" + entry.getKey() + "': " + group.getTargets().size() + " targets");
            }
        }
    } catch (Exception e) {
        getLog().error("Error generating target groups for " + project.getArtifactId() + ": " + e.getMessage(), e);
        // Continue with empty target groups
        projectTargetGroups.put(project, new LinkedHashMap<>());
    }
}

