 26/**
 27 * Maven plugin that uses MavenSession API to analyze Maven projects for Nx integration
 28 */
 29@Mojo(name = "analyze", aggregator = true, requiresDependencyResolution = ResolutionScope.COMPILE_PLUS_RUNTIME)
 30public class NxAnalyzerMojo extends AbstractMojo {
 31    
 32    @Parameter(defaultValue = "${session}", readonly = true, required = true)
 33    private MavenSession session;
 34    
 35    @Parameter(defaultValue = "${reactorProjects}", readonly = true, required = true)
 36    private List<MavenProject> reactorProjects;
 37    
 38    @Component
 39    private LifecycleExecutor lifecycleExecutor;
 40    
 41    @Parameter(property = "nx.outputFile")
 42    private String outputFile;
 43    
 44    @Parameter(property = "nx.verbose", defaultValue = "false")
 45    private String verboseStr;
 46    
 47    // Performance optimization caches
 48    private final Map<String, MavenExecutionPlan> executionPlanCache = new ConcurrentHashMap<>();
 49    private final Map<String, Map<String, String>> phaseGoalMappingCache = new ConcurrentHashMap<>();
 50    
 51    private boolean isVerbose() {
 52        // Check both the parameter and system property
 53        String systemProp = System.getProperty("nx.verbose");
 54        boolean fromParam = "true".equalsIgnoreCase(verboseStr);
 55        boolean fromSystem = "true".equalsIgnoreCase(systemProp);
 56        return fromParam || fromSystem;
 57    }
 58    
 59    @Override
 60    public void execute() throws MojoExecutionException, MojoFailureException {
 61        long startTime = System.currentTimeMillis();
 62        
 63        try {
 64            getLog().info("Starting Nx analysis using MavenSession with dependency resolution...");
 65            String systemProp = System.getProperty("nx.verbose");
 66            getLog().info("Debug: Verbose param='" + verboseStr + "' system='" + systemProp + "' -> " + isVerbose());
 67            
 68            // Use reactor projects from Maven session - these are the properly resolved projects
 69            List<MavenProject> projects = reactorProjects;
 70            getLog().info("Found " + projects.size() + " projects in reactor");
 71            
 72            if (isVerbose()) {
 73                getLog().info("Verbose mode enabled - detailed progress will be shown");
 74            }
 75            
 76            // Always show some basic details - the condition was checking verbose but should always show
 77            getLog().info("Root directory: " + session.getExecutionRootDirectory());
 78            if (projects.size() > 0) {
 79                getLog().info("First project: " + projects.get(0).getGroupId() + ":" + projects.get(0).getArtifactId());
 80                getLog().info("Last project: " + projects.get(projects.size()-1).getGroupId() + ":" + projects.get(projects.size()-1).getArtifactId());
 81            }
 82            
 83            // Convert Maven projects to Nx format
 84            Map<String, Object> result = convertToNxFormat(projects);
 85            
 86            // Write output
 87            String outputPath = determineOutputPath();
 88            if (isVerbose()) {
 89                getLog().info("Writing results to: " + outputPath);
 90            }
 91            writeResult(result, outputPath);
 92            
 93            long totalTime = System.currentTimeMillis() - startTime;
 94            double projectsPerSecond = projects.size() / (totalTime / 1000.0);
 95            
 96            getLog().info("Analysis complete. Results written to: " + outputPath);
 97            if (isVerbose()) {
 98                getLog().info("Performance: Processed " + projects.size() + " projects in " + 
 99                             (totalTime / 1000.0) + "s (" + String.format("%.1f", projectsPerSecond) + " projects/sec)");
100            }
101            getLog().info("SUCCESS: Maven analysis completed successfully");
102            
103        } catch (Exception e) {
104            throw new MojoExecutionException("Analysis failed: " + e.getMessage(), e);
