108    private Map<String, Object> convertToNxFormat(List<MavenProject> projects) {
109        long startTime = System.currentTimeMillis();
110        
111        if (isVerbose()) {
112            getLog().info("Target generation phase: Processing " + projects.size() + " projects...");
113        }
114        
115        // Pre-compute execution plan for common project types to enable caching
116        if (isVerbose()) {
117            getLog().info("Pre-computing execution plans for caching...");
118        }
119        precomputeExecutionPlans();
120        
121        // Generate targets for each project using parallel streams for better performance
122        Map<MavenProject, Map<String, TargetConfiguration>> projectTargets = new ConcurrentHashMap<>();
123        Map<MavenProject, Map<String, TargetGroup>> projectTargetGroups = new ConcurrentHashMap<>();
124        
125        // Progress tracking for parallel processing
126        AtomicInteger processedCount = new AtomicInteger(0);
127        AtomicInteger totalTargets = new AtomicInteger(0);
128        
129        if (isVerbose()) {
130            getLog().info("Starting parallel target generation for " + projects.size() + " projects...");
131        }
132        
133        // Use parallel streams for target generation
134        projects.parallelStream().forEach(project -> {
135            try {
136                Map<String, TargetConfiguration> targets = generateTargets(project);
137                projectTargets.put(project, targets);
138                totalTargets.addAndGet(targets.size());
139                
140                // Generate target groups using cached execution plans
141                Map<String, TargetGroup> targetGroups = generateTargetGroupsOptimized(project, targets);
142                projectTargetGroups.put(project, targetGroups);
143                
144                int processed = processedCount.incrementAndGet();
145                
146                // Show progress for large workspaces
147                if (isVerbose() && projects.size() > 50 && processed % 50 == 0) {
148                    long elapsed = System.currentTimeMillis() - startTime;
149                    double rate = processed / (elapsed / 1000.0);
150                    double percentage = processed * 100.0 / projects.size();
151                    
152                    getLog().info("Target generation progress: " + processed + "/" + projects.size() + 
153                                 " (" + String.format("%.1f", percentage) + "%) - " +
154                                 String.format("%.1f", rate) + " projects/sec");
155                } else if (isVerbose() && projects.size() <= 50) {
156                    getLog().info("Processed project " + processed + "/" + projects.size() + 
157                                 ": " + project.getGroupId() + ":" + project.getArtifactId() + 
158                                 " (" + targets.size() + " targets, " + targetGroups.size() + " groups)");
159                }
160                
161            } catch (Exception e) {
162                getLog().error("Error processing project " + project.getArtifactId() + ": " + e.getMessage(), e);
163                // Continue with empty targets and groups
164                projectTargets.put(project, new LinkedHashMap<>());
165                projectTargetGroups.put(project, new LinkedHashMap<>());
166                processedCount.incrementAndGet();
167            }
168        });
169        
170        long targetGenTime = System.currentTimeMillis() - startTime;
171        if (isVerbose()) {
172            getLog().info("Target generation complete: " + totalTargets.get() + " targets created in " + 
173                         (targetGenTime / 1000.0) + "s using parallel processing");
174        }
175        
176        // Memory optimization: suggest GC after intensive target generation for large projects
177        if (projects.size() > 100) {
178            if (isVerbose()) {
179                getLog().info("Suggesting garbage collection after processing " + projects.size() + " projects");
180            }
181            System.gc();
182            Thread.yield(); // Allow GC to run
183        }
184        
185        // Generate Nx-compatible outputs
186        File workspaceRoot = new File(session.getExecutionRootDirectory());
187        
188        if (isVerbose()) {
189            getLog().info("Generating CreateNodes results for " + projects.size() + " projects...");
190        }
191        long createNodesStart = System.currentTimeMillis();
192        List<CreateNodesV2Entry> createNodesEntries = CreateNodesResultGenerator.generateCreateNodesV2Results(projects, workspaceRoot, projectTargets, projectTargetGroups);
193        List<Object[]> createNodesResults = new ArrayList<>();
194        for (CreateNodesV2Entry entry : createNodesEntries) {
195            createNodesResults.add(entry.toArray());
196        }
197        long createNodesTime = System.currentTimeMillis() - createNodesStart;
198        
199        if (isVerbose()) {
200            getLog().info("Generating dependency graph for " + projects.size() + " projects...");
201        }
202        long dependenciesStart = System.currentTimeMillis();
203        List<RawProjectGraphDependency> createDependencies = CreateDependenciesGenerator.generateCreateDependencies(projects, workspaceRoot, getLog(), isVerbose());
204        long dependenciesTime = System.currentTimeMillis() - dependenciesStart;
205        
206        if (isVerbose()) {
207            getLog().info("CreateNodes generation: " + (createNodesTime / 1000.0) + "s");
208            getLog().info("Dependencies generation: " + (dependenciesTime / 1000.0) + "s");
209            getLog().info("Found " + createDependencies.size() + " workspace dependencies");
210        }
211        
212        Map<String, Object> result = new LinkedHashMap<>();
213        result.put("createNodesResults", createNodesResults);
214        result.put("createDependencies", createDependencies);
215        
216        return result;
217    }
