514    /**
515     * Pre-compute execution plans for common project configurations to enable caching
516     */
517    private void precomputeExecutionPlans() {
518        try {
519            long startTime = System.currentTimeMillis();
520            
521            // Pre-compute execution plans for common Maven phases
522            String[] commonPhases = {"compile", "test", "package", "verify"};
523            for (String phase : commonPhases) {
524                String cacheKey = "common-" + phase;
525                if (!executionPlanCache.containsKey(cacheKey)) {
526                    try {
527                        MavenExecutionPlan plan = lifecycleExecutor.calculateExecutionPlan(session, phase);
528                        executionPlanCache.put(cacheKey, plan);
529                        
530                        // Build phase-to-goal mapping for fast lookup
531                        Map<String, String> goalToPhaseMap = new LinkedHashMap<>();
532                        for (MojoExecution execution : plan.getMojoExecutions()) {
533                            String goal = execution.getGoal();
534                            String executionPhase = execution.getLifecyclePhase();
535                            if (executionPhase != null && !executionPhase.isEmpty()) {
536                                goalToPhaseMap.put(goal, executionPhase);
537                                
538                                // Also map plugin:goal format
539                                String pluginKey = execution.getPlugin().getGroupId() + ":" + execution.getPlugin().getArtifactId();
540                                String targetName = getTargetName(execution.getPlugin().getArtifactId(), goal);
541                                goalToPhaseMap.put(targetName, executionPhase);
542                            }
543                        }
544                        phaseGoalMappingCache.put(cacheKey, goalToPhaseMap);
545                        
546                    } catch (Exception e) {
547                        if (isVerbose()) {
548                            getLog().warn("Could not pre-compute execution plan for phase " + phase + ": " + e.getMessage());
549                        }
550                    }
551                }
552            }
553            
554            long elapsed = System.currentTimeMillis() - startTime;
555            if (isVerbose()) {
556                getLog().info("Pre-computed " + executionPlanCache.size() + " execution plans in " + 
557                             elapsed + "ms for performance optimization");
558            }
559            
560        } catch (Exception e) {
561            if (isVerbose()) {
562                getLog().warn("Error during execution plan pre-computation: " + e.getMessage());
563            }
564        }
565    }
