570    private Map<String, TargetGroup> generateTargetGroupsOptimized(MavenProject project, Map<String, TargetConfiguration> projectTargets) {
571        Map<String, TargetGroup> targetGroups = new LinkedHashMap<>();
572        
573        // Define Maven lifecycle phases with their order
574        String[] phases = {
575            "clean", "validate", "compile", "test", "package", 
576            "verify", "install", "deploy", "site"
577        };
578        
579        Map<String, String> phaseDescriptions = new LinkedHashMap<>();
580        phaseDescriptions.put("clean", "Clean up artifacts created by build");
581        phaseDescriptions.put("validate", "Validate project structure and configuration");
582        phaseDescriptions.put("compile", "Compile source code");
583        phaseDescriptions.put("test", "Run unit tests");
584        phaseDescriptions.put("package", "Package compiled code");
585        phaseDescriptions.put("verify", "Verify package integrity");
586        phaseDescriptions.put("install", "Install package to local repository");
587        phaseDescriptions.put("deploy", "Deploy package to remote repository");
588        phaseDescriptions.put("site", "Generate project documentation");
589        
590        // Create target groups for each phase
591        for (int i = 0; i < phases.length; i++) {
592            String phase = phases[i];
593            TargetGroup group = new TargetGroup(phase, phaseDescriptions.get(phase), i);
594            targetGroups.put(phase, group);
595        }
596        
597        // Use cached phase-to-goal mappings for fast assignment
598        Map<String, String> cachedMapping = getCachedPhaseGoalMapping();
599        
600        for (String targetName : projectTargets.keySet()) {
601            TargetConfiguration target = projectTargets.get(targetName);
602            String assignedPhase = null;
603            
604            // First try cached mapping for O(1) lookup
605            if (cachedMapping.containsKey(targetName)) {
606                assignedPhase = cachedMapping.get(targetName);
607            } else if (target.getMetadata() != null && target.getMetadata().getPhase() != null) {
608                // Use metadata phase info
609                assignedPhase = target.getMetadata().getPhase();
610            } else {
611                // Fallback to pattern matching
612                assignedPhase = assignPhaseByPattern(targetName, phases);
613            }
614            
615            // Add target to the appropriate group
616            TargetGroup group = targetGroups.get(assignedPhase);
617            if (group != null) {
618                group.addTarget(targetName);
619            }
620        }
621        
622        return targetGroups;
623    }
