640     * Assign phase based on target name patterns (fallback method)
641     */
642    private String assignPhaseByPattern(String targetName, String[] phases) {
643        // Check if target name matches a phase directly
644        for (String phase : phases) {
645            if (targetName.equals(phase)) {
646                return phase;
647            }
648        }
649        
650        // Pattern-based assignment
651        if (targetName.contains("test")) {
652            return "test";
653        } else if (targetName.contains("package") || targetName.contains("jar") || targetName.contains("war")) {
654            return "package";
655        } else if (targetName.contains("clean")) {
656            return "clean";
657        } else if (targetName.contains("site") || targetName.contains("javadoc")) {
658            return "site";
659        } else {
660            return "compile"; // Default fallback
661        }
662    }
