664    private Map<String, TargetGroup> generateTargetGroups(MavenProject project, Map<String, TargetConfiguration> projectTargets) {
665        Map<String, TargetGroup> targetGroups = new LinkedHashMap<>();
666        
667        // Define Maven lifecycle phases with their order
668        String[] phases = {
669            "clean", "validate", "compile", "test", "package", 
670            "verify", "install", "deploy", "site"
671        };
672        
673        Map<String, String> phaseDescriptions = new LinkedHashMap<>();
674        phaseDescriptions.put("clean", "Clean up artifacts created by build");
675        phaseDescriptions.put("validate", "Validate project structure and configuration");
676        phaseDescriptions.put("compile", "Compile source code");
677        phaseDescriptions.put("test", "Run unit tests");
678        phaseDescriptions.put("package", "Package compiled code");
679        phaseDescriptions.put("verify", "Verify package integrity");
680        phaseDescriptions.put("install", "Install package to local repository");
681        phaseDescriptions.put("deploy", "Deploy package to remote repository");
682        phaseDescriptions.put("site", "Generate project documentation");
683        
684        // Create target groups for each phase
685        for (int i = 0; i < phases.length; i++) {
686            String phase = phases[i];
687            TargetGroup group = new TargetGroup(phase, phaseDescriptions.get(phase), i);
688            targetGroups.put(phase, group);
689        }
690        
691        // Try to get execution plan to assign plugin goals to phases
692        try {
693            MavenExecutionPlan executionPlan = lifecycleExecutor.calculateExecutionPlan(session, "package");
694            
695            if (isVerbose()) {
696                getLog().info("Using execution plan to assign targets to phase groups");
697            }
698            
699            // Map plugin executions to phases
700            Map<String, String> goalToPhaseMap = new LinkedHashMap<>();
701            for (MojoExecution execution : executionPlan.getMojoExecutions()) {
702                String goal = execution.getGoal();
703                String phase = execution.getLifecyclePhase();
704                if (phase != null && !phase.isEmpty()) {
705                    goalToPhaseMap.put(goal, phase);
706                    
707                    // Also map plugin:goal format
708                    String pluginKey = execution.getPlugin().getGroupId() + ":" + execution.getPlugin().getArtifactId();
709                    String targetName = getTargetName(execution.getPlugin().getArtifactId(), goal);
710                    goalToPhaseMap.put(targetName, phase);
711                }
712            }
713            
714            // Assign targets to groups based on execution plan
715            for (String targetName : projectTargets.keySet()) {
716                TargetConfiguration target = projectTargets.get(targetName);
717                String assignedPhase = null;
718                
719                // Check if target metadata has phase info
720                if (target.getMetadata() != null && target.getMetadata().getPhase() != null) {
721                    assignedPhase = target.getMetadata().getPhase();
722                } else {
723                    // Try to match by goal name
724                    for (Map.Entry<String, String> entry : goalToPhaseMap.entrySet()) {
725                        if (targetName.contains(entry.getKey()) || targetName.equals(entry.getKey())) {
726                            assignedPhase = entry.getValue();
727                            break;
728                        }
729                    }
730                }
731                
732                // Default phase assignment for targets without execution plan info
733                if (assignedPhase == null) {
734                    if (Arrays.asList(phases).contains(targetName)) {
735                        assignedPhase = targetName; // Phase targets go to their own group
736                    } else {
737                        assignedPhase = "compile"; // Default fallback
738                    }
739                }
740                
741                // Add target to the appropriate group
742                TargetGroup group = targetGroups.get(assignedPhase);
743                if (group != null) {
744                    group.addTarget(targetName);
745                }
746            }
747            
748        } catch (Exception e) {
749            if (isVerbose()) {
750                getLog().warn("Could not get execution plan, using fallback target grouping: " + e.getMessage());
751            }
752            
753            // Fallback: assign targets based on naming patterns
754            for (String targetName : projectTargets.keySet()) {
755                String assignedPhase = "compile"; // Default
756                
757                // Assign based on target name patterns
758                if (Arrays.asList(phases).contains(targetName)) {
759                    assignedPhase = targetName;
760                } else if (targetName.contains("test")) {
761                    assignedPhase = "test";
762                } else if (targetName.contains("package") || targetName.contains("jar") || targetName.contains("war")) {
763                    assignedPhase = "package";
764                } else if (targetName.contains("clean")) {
765                    assignedPhase = "clean";
766                } else if (targetName.contains("site") || targetName.contains("javadoc")) {
767                    assignedPhase = "site";
768                }
769                
770                TargetGroup group = targetGroups.get(assignedPhase);
771                if (group != null) {
772                    group.addTarget(targetName);
773                }
774            }
775        }
776        
777        return targetGroups;
778    }
779    
780    private String determineOutputPath() {
781        if (outputFile != null && !outputFile.isEmpty()) {
782            return outputFile;
783        }
784        
785        // Default to workspace root
786        return new File(session.getExecutionRootDirectory(), "maven-analysis.json").getAbsolutePath();
787    }
788    
789
790    
791    
792    private void writeResult(Map<String, Object> result, String outputPath) throws IOException {
793        if (isVerbose()) {
794            getLog().info("Writing analysis results to: " + outputPath);
795        }
796        
797        // Memory optimization: use buffered writer for large JSON files
798        Gson gson = new GsonBuilder()
799            .setPrettyPrinting()
800            .serializeNulls() // Handle null values consistently
801            .create();
802            
803        try (java.io.BufferedWriter bufferedWriter = new java.io.BufferedWriter(
804                new FileWriter(outputPath), 65536)) { // 64KB buffer for better I/O performance
805            
806            gson.toJson(result, bufferedWriter);
807            bufferedWriter.flush();
808            
809            if (isVerbose()) {
810                long fileSize = new File(outputPath).length();
811                getLog().info("Successfully wrote " + (fileSize / 1024) + "KB analysis results");
812            }
813        }
814        
815        // Memory optimization: suggest GC after large JSON write operations
816        long fileSize = new File(outputPath).length();
817        if (fileSize > 10 * 1024 * 1024) { // > 10MB
818            if (isVerbose()) {
819                getLog().info("Large output file (" + (fileSize / 1024 / 1024) + "MB) - suggesting garbage collection");
820            }
821            System.gc();
822        }
823    }
824}

