 12	import { dirname, join, relative } from 'path';
 13	import { existsSync, readFileSync, unlinkSync } from 'fs';
 14	import { exec, execSync, spawn } from 'child_process';
 15	import { promisify } from 'util';
 16	
 17	const execAsync = promisify(exec);
 18	
 19	export interface MavenPluginOptions {
 20	  buildTargetName?: string;
 21	  testTargetName?: string;
 22	  serveTargetName?: string;
 23	  javaExecutable?: string;
 24	  mavenExecutable?: string;
 25	  compilerArgs?: string[];
 26	}
 27	
 28	const DEFAULT_OPTIONS: MavenPluginOptions = {
 29	  buildTargetName: 'build',
 30	  testTargetName: 'test',
 31	  serveTargetName: 'serve',
 32	  javaExecutable: 'java',
 33	  mavenExecutable: 'mvn',
 34	  compilerArgs: [],
 35	};
