 37	/**
 38	 * Maven plugin that uses a Java program to analyze pom.xml files
 39	 * and generate Nx project configurations with comprehensive dependencies
 40	 */
 41	export const createNodesV2: CreateNodesV2 = [
 42	  '**/pom.xml',
 43	  async (configFiles, options, context) => {
 44	    const opts: MavenPluginOptions = Object.assign({}, DEFAULT_OPTIONS, options || {});
 45	
 46	    console.log(`All config files found: ${JSON.stringify(configFiles.slice(0, 10), null, 2)}`);
 47	
 48	    // Filter out the maven-script pom.xml and build artifact directories  
 49	    const filteredConfigFiles = configFiles.filter(file =>
 50	      !file.includes('maven-script/pom.xml') &&
